export interface TeaRecord {
  id: string;
  brand: string;
  name: string;
  sugar: string; // e.g., "30%", "Regular", "Zero"
  ice: string;   // e.g., "None", "Less", "Regular"
  price: number;
  rating: number; // 1-5
  remark: string;
  timestamp: number;
  imageUrl?: string; // base64 or url
  tags?: string[];
}

export type Screen = 'calendar' | 'record' | 'stats' | 'library';

export interface BrandStat {
  name: string;
  count: number;
  avgRating: number;
}

export interface DayStat {
  date: string;
  count: number;
}

import { TeaRecord } from '../types';

const STORAGE_KEY = 'bubble_tea_journal_v1';

export const saveRecords = (records: TeaRecord[]) => {
  try {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(records));
  } catch (e) {
    console.error("Failed to save records", e);
  }
};

export const loadRecords = (): TeaRecord[] => {
  try {
    const data = localStorage.getItem(STORAGE_KEY);
    return data ? JSON.parse(data) : [];
  } catch (e) {
    console.error("Failed to load records", e);
    return [];
  }
};

// Seed some fake data if empty for better first-time UX
export const seedInitialData = (): TeaRecord[] => {
  const existing = loadRecords();
  if (existing.length > 0) return existing;

  const now = Date.now();
  const day = 86400000;

  const mock: TeaRecord[] = [
    {
      id: '1',
      brand: 'HeyTea',
      name: 'Very Grape Cheezo',
      sugar: 'Less Sugar',
      ice: 'Less Ice',
      price: 28,
      rating: 5,
      remark: 'Classic flavor, cheese foam is perfect today.',
      timestamp: now - day * 2,
    },
    {
      id: '2',
      brand: 'Coco',
      name: 'Bubble Milk Tea',
      sugar: '50%',
      ice: 'Regular',
      price: 15,
      rating: 4,
      remark: 'Good value, pearls are chewy.',
      timestamp: now - day * 5,
    },
    {
      id: '3',
      brand: 'Gong Cha',
      name: 'Oolong Milk Tea',
      sugar: '30%',
      ice: 'No Ice',
      price: 18,
      rating: 3,
