import React, { useState, useRef } from 'react';
import { Camera, Upload, X, Loader2, Sparkles, Check } from 'lucide-react';
import { TeaRecord } from '../types';
import { analyzeTeaImage } from '../services/geminiService';

interface RecordFormProps {
  onSave: (record: Omit<TeaRecord, 'id' | 'timestamp'>) => void;
  onCancel: () => void;
}

const COMMON_SUGAR = ['Regular', '70%', '50%', '30%', 'Zero'];
const COMMON_ICE = ['Regular', 'Less', 'No Ice', 'Hot'];

export const RecordForm: React.FC<RecordFormProps> = ({ onSave, onCancel }) => {
  const [loading, setLoading] = useState(false);
  const [imagePreview, setImagePreview] = useState<string | null>(null);

  const [formData, setFormData] = useState({
    brand: '',
    name: '',
    sugar: '',
    ice: '',
    price: '',
    rating: 0,
    remark: '',
  });

  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = async () => {
      const base64 = reader.result as string;
      setImagePreview(base64);

      // Auto-analyze
      setLoading(true);
      try {
        const info = await analyzeTeaImage(base64);
        setFormData(prev => ({
            ...prev,
            brand: info.brand || prev.brand,
            name: info.name || prev.name,
            sugar: info.sugar || prev.sugar,
            ice: info.ice || prev.ice,
            price: info.price ? String(info.price) : prev.price
        }));
      } catch (err) {
        console.error("AI Analysis failed, manual entry required");
      } finally {
        setLoading(false);
      }
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSave({
      brand: formData.brand || 'Unknown Brand',
      name: formData.name || 'Unknown Drink',
      sugar: formData.sugar,
      ice: formData.ice,
      price: parseFloat(formData.price) || 0,
      rating: formData.rating,
      remark: formData.remark,
      imageUrl: imagePreview || undefined,
    });
  };

  return (
    <div className="bg-cream min-h-full pb-24 animate-fade-in">
      <div className="sticky top-0 bg-cream/95 backdrop-blur z-20 px-6 py-4 flex justify-between items-center border-b border-milk-tea-100">
        <button onClick={onCancel} className="p-2 -ml-2 text-milk-tea-500">
            <X size={24} />
        </button>
        <h2 className="text-lg font-bold text-milk-tea-900">New Poke</h2>
        <button 
            onClick={handleSubmit}
            className="px-4 py-1.5 bg-milk-tea-800 text-cream rounded-full text-sm font-semibold shadow-md active:scale-95 transition-transform"
        >
            Save
        </button>
      </div>

      <div className="p-6 space-y-6 max-w-md mx-auto">
        {/* Image Section */}
        <div className="relative group">
            <input 
                type="file" 
                ref={fileInputRef} 
                onChange={handleFileChange} 
                accept="image/*" 
                className="hidden" 
            />

            <div 
                onClick={() => fileInputRef.current?.click()}
                className={`w-full aspect-[4/3] rounded-3xl border-2 border-dashed flex flex-col items-center justify-center cursor-pointer transition-all overflow-hidden ${
                    imagePreview ? 'border-transparent' : 'border-milk-tea-200 bg-milk-tea-100/30 hover:bg-milk-tea-100/50'
                }`}
            >
                {imagePreview ? (
                    <>
                        <img src={imagePreview} alt="Preview" className="w-full h-full object-cover" />
                        <div className="absolute inset-0 bg-black/20 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                            <span className="text-white text-sm font-medium">Change Photo</span>
                        </div>
                    </>
                ) : (
                    <div className="text-milk-tea-400 flex flex-col items-center gap-2">
                        <Camera size={32} />
                        <span className="text-sm">Tap to scan cup/receipt</span>
                    </div>
                )}
            </div>

            {loading && (
                <div className="absolute inset-0 bg-cream/80 flex flex-col items-center justify-center rounded-3xl z-10 backdrop-blur-sm">
                    <Loader2 className="animate-spin text-milk-tea-800 mb-2" size={32} />
                    <p className="text-milk-tea-800 text-sm font-medium animate-pulse flex items-center gap-1">
                        <Sparkles size={14} /> AI Identifying...
                    </p>
                </div>
            )}
        </div>

        {/* Form Fields */}
        <div className="space-y-5">
            {/* Brand & Name */}
            <div className="space-y-3">
                <div className="space-y-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Brand</label>
                    <input
                        type="text"
                        value={formData.brand}
                        onChange={(e) => setFormData({...formData, brand: e.target.value})}
                        placeholder="e.g. HeyTea"
                        className="w-full p-4 bg-white border border-milk-tea-100 rounded-2xl focus:outline-none focus:ring-2 focus:ring-milk-tea-300 text-milk-tea-900 placeholder:text-milk-tea-200"
                    />
                </div>
                <div className="space-y-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Drink Name</label>
                    <input
                        type="text"
                        value={formData.name}
                        onChange={(e) => setFormData({...formData, name: e.target.value})}
                        placeholder="e.g. Grape Cheezo"
                        className="w-full p-4 bg-white border border-milk-tea-100 rounded-2xl focus:outline-none focus:ring-2 focus:ring-milk-tea-300 text-milk-tea-900 placeholder:text-milk-tea-200"
                    />
                </div>
            </div>

            {/* Options Row */}
            <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Sugar</label>
                     <div className="relative">
                        <select 
                            value={formData.sugar}
                            onChange={(e) => setFormData({...formData, sugar: e.target.value})}
                            className="w-full p-3 bg-white border border-milk-tea-100 rounded-xl appearance-none text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-300"
                        >
                            <option value="">Select</option>
                            {COMMON_SUGAR.map(s => <option key={s} value={s}>{s}</option>)}
                        </select>
                        <div className="absolute right-3 top-1/2 -translate-y-1/2 pointer-events-none text-milk-tea-400">▼</div>
                     </div>
                </div>
                <div className="space-y-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Ice</label>
                    <div className="relative">
                        <select 
                            value={formData.ice}
                            onChange={(e) => setFormData({...formData, ice: e.target.value})}
                            className="w-full p-3 bg-white border border-milk-tea-100 rounded-xl appearance-none text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-300"
                        >
                            <option value="">Select</option>
                            {COMMON_ICE.map(s => <option key={s} value={s}>{s}</option>)}
                        </select>
                        <div className="absolute right-3 top-1/2 -translate-y-1/2 pointer-events-none text-milk-tea-400">▼</div>
                    </div>
                </div>
            </div>

            {/* Price & Rating */}
            <div className="flex gap-4 items-end">
                <div className="space-y-1 flex-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Price (¥)</label>
                    <input
                        type="number"
                        value={formData.price}
                        onChange={(e) => setFormData({...formData, price: e.target.value})}
                        placeholder="0.00"
                        className="w-full p-3 bg-white border border-milk-tea-100 rounded-xl focus:outline-none focus:ring-2 focus:ring-milk-tea-300 text-milk-tea-900"
                    />
                </div>
                <div className="flex-1 space-y-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Rating</label>
                    <div className="flex justify-between bg-white p-3 rounded-xl border border-milk-tea-100">
                        {[1, 2, 3, 4, 5].map((star) => (
                            <button
                                key={star}
                                type="button"
                                onClick={() => setFormData({...formData, rating: star})}
                                className={`text-xl transition-transform active:scale-125 ${star <= formData.rating ? 'text-yellow-400' : 'text-gray-200'}`}
                            >
                                ★
                            </button>
                        ))}
                    </div>
