            </div>

            {/* Remark */}
            <div className="space-y-1">
                <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Remark</label>
                <textarea
                    value={formData.remark}
                    onChange={(e) => setFormData({...formData, remark: e.target.value})}
                    placeholder="How was the texture? Flavor?"
                    rows={3}
                    className="w-full p-4 bg-white border border-milk-tea-100 rounded-2xl focus:outline-none focus:ring-2 focus:ring-milk-tea-300 text-milk-tea-900 resize-none placeholder:text-milk-tea-200"
                />
            </div>
        </div>
      </div>
    </div>
  );
};

import React from 'react';
import { TeaRecord } from '../types';

interface CalendarViewProps {
  records: TeaRecord[];
}

export const CalendarView: React.FC<CalendarViewProps> = ({ records }) => {
  const today = new Date();
  const [currentMonth, setCurrentMonth] = React.useState(today);

  // Helper to get days in month
  const getDaysInMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
  };

  const daysInMonth = getDaysInMonth(currentMonth);
  const firstDay = getFirstDayOfMonth(currentMonth);

  // Group records by day
  const recordsByDay = records.reduce((acc, record) => {
    const d = new Date(record.timestamp);
    if (d.getMonth() === currentMonth.getMonth() && d.getFullYear() === currentMonth.getFullYear()) {
      const day = d.getDate();
      if (!acc[day]) acc[day] = [];
      acc[day].push(record);
    }
    return acc;
  }, {} as Record<number, TeaRecord[]>);

  const renderStars = (rating: number) => {
    return "★".repeat(rating) + "☆".repeat(5 - rating);
  };

  return (
    <div className="p-6 max-w-md mx-auto animate-fade-in">
        <header className="mb-8 flex justify-between items-baseline">
            <h1 className="text-3xl font-bold text-milk-tea-900">Tea Journal</h1>
            <p className="text-milk-tea-500 text-sm font-medium">{currentMonth.toLocaleString('default', { month: 'long', year: 'numeric' })}</p>
        </header>

      {/* Calendar Grid */}
      <div className="bg-white rounded-3xl p-6 shadow-sm shadow-milk-tea-100 mb-8">
        <div className="grid grid-cols-7 gap-2 mb-4">
          {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map(d => (
            <div key={d} className="text-center text-xs text-milk-tea-400 font-bold">{d}</div>
          ))}
        </div>
        <div className="grid grid-cols-7 gap-y-4 gap-x-2">
          {Array.from({ length: firstDay }).map((_, i) => (
            <div key={`empty-${i}`} />
          ))}
          {Array.from({ length: daysInMonth }).map((_, i) => {
            const day = i + 1;
            const hasTea = recordsByDay[day]?.length > 0;
            return (
              <div key={day} className="flex flex-col items-center gap-1">
                <span className={`text-sm font-medium w-8 h-8 flex items-center justify-center rounded-full ${hasTea ? 'bg-milk-tea-100 text-milk-tea-800' : 'text-milk-tea-300'}`}>
                  {day}
                </span>
                {hasTea && (
                    <div className="flex gap-0.5">
                        {recordsByDay[day].slice(0, 3).map((r, idx) => (
                            <div key={idx} className={`w-1.5 h-1.5 rounded-full ${r.rating >= 4 ? 'bg-milk-tea-800' : 'bg-milk-tea-500'}`} />
                        ))}
                    </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      {/* Recent Records List */}
      <h3 className="text-lg font-bold text-milk-tea-800 mb-4 px-1">Recent Sips</h3>
      <div className="space-y-4">
        {records.sort((a,b) => b.timestamp - a.timestamp).map(record => (
          <div key={record.id} className="bg-white p-4 rounded-2xl flex gap-4 shadow-sm shadow-milk-tea-100 border border-transparent hover:border-milk-tea-200 transition-colors">
            <div className="w-16 h-16 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative">
                {record.imageUrl ? (
                    <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                ) : (
                    <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-2xl font-bold opacity-50">
                        {record.brand[0]}
                    </div>
                )}
            </div>
            <div className="flex-1 min-w-0">
              <div className="flex justify-between items-start">
                <div>
                    <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                    <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                </div>
                <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
              </div>

              <div className="mt-2 flex flex-wrap gap-2">
                <span className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                    {record.sugar || 'Regular'}
                </span>
                <span className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                    {record.ice || 'Regular'}
                </span>
              </div>
            </div>
            <div className="flex flex-col justify-end text-right">
                <span className="text-sm font-bold text-milk-tea-800">¥{record.price}</span>
            </div>
          </div>
        ))}
        {records.length === 0 && (
            <div className="text-center py-12 text-milk-tea-300">
                <p>No bubble tea yet!</p>
                <p className="text-sm mt-2">Tap + to add your first drink.</p>
            </div>
        )}
      </div>
    </div>
  );
};

import React from 'react';
import { PieChart, Pie, Cell, BarChart, Bar, XAxis, Tooltip, ResponsiveContainer } from 'recharts';
import { TeaRecord } from '../types';

interface StatsViewProps {
  records: TeaRecord[];
}

const COLORS = ['#C89F8D', '#D3E0C8', '#E0D6E9', '#F4DCCF', '#A3B18A'];

export const StatsView: React.FC<StatsViewProps> = ({ records }) => {
  // Brand Distribution
  const brandData = Object.values(records.reduce((acc, curr) => {
    if (!acc[curr.brand]) acc[curr.brand] = { name: curr.brand, value: 0 };
    acc[curr.brand].value += 1;
    return acc;
  }, {} as Record<string, { name: string; value: number }>));

  // Weekly Frequency (Mock logic for "Last 7 Days")
  const last7Days = Array.from({ length: 7 }).map((_, i) => {
    const d = new Date();
    d.setDate(d.getDate() - (6 - i));
    const dateStr = d.toLocaleDateString('en-US', { weekday: 'short' });

    const count = records.filter(r => {
      const rd = new Date(r.timestamp);
      return rd.getDate() === d.getDate() && rd.getMonth() === d.getMonth();
    }).length;

    return { name: dateStr, count };
  });

  const totalSpent = records.reduce((sum, r) => sum + r.price, 0);
  const avgRating = records.length > 0 ? (records.reduce((sum, r) => sum + r.rating, 0) / records.length).toFixed(1) : '0';

  return (
    <div className="p-6 pb-24 max-w-md mx-auto space-y-8 animate-fade-in">
      <header>
         <h1 className="text-3xl font-bold text-milk-tea-900 mb-2">Insights</h1>
         <div className="flex gap-4">
             <div className="flex-1 bg-white p-4 rounded-2xl shadow-sm border border-milk-tea-100">
                 <p className="text-xs text-milk-tea-400 uppercase font-bold">Total Spent</p>
