const COLORS = ['#C89F8D', '#D3E0C8', '#E0D6E9', '#F4DCCF', '#A3B18A'];

export const StatsView: React.FC<StatsViewProps> = ({ records }) => {
  // Brand Distribution
  const brandStatsMap = records.reduce((acc, curr) => {
    if (!acc[curr.brand]) acc[curr.brand] = { name: curr.brand, value: 0 };
    acc[curr.brand].value += 1;
    return acc;
  }, {} as Record<string, { name: string; value: number }>);

  const brandData: { name: string; value: number }[] = Object.values(brandStatsMap);

  // Weekly Frequency (Mock logic for "Last 7 Days")
  const last7Days = Array.from({ length: 7 }).map((_, i) => {
    const d = new Date();
    d.setDate(d.getDate() - (6 - i));
    const dateStr = d.toLocaleDateString('en-US', { weekday: 'short' });

    const count = records.filter(r => {
      const rd = new Date(r.timestamp);
      return rd.getDate() === d.getDate() && rd.getMonth() === d.getMonth();
    }).length;

    return { name: dateStr, count };
  });

  const totalSpent = records.reduce((sum, r) => sum + r.price, 0);
  const avgRating = records.length > 0 ? (records.reduce((sum, r) => sum + r.rating, 0) / records.length).toFixed(1) : '0';

  return (
    <div className="p-6 pb-24 max-w-md mx-auto space-y-8 animate-fade-in">
      <header>
         <h1 className="text-3xl font-bold text-milk-tea-900 mb-2">Insights</h1>
         <div className="flex gap-4">
             <div className="flex-1 bg-white p-4 rounded-2xl shadow-sm border border-milk-tea-100">
                 <p className="text-xs text-milk-tea-400 uppercase font-bold">Total Spent</p>
