             </div>
             <div className="flex-1 bg-white p-4 rounded-2xl shadow-sm border border-milk-tea-100">
                 <p className="text-xs text-milk-tea-400 uppercase font-bold">Avg Rating</p>
                 <p className="text-2xl font-bold text-milk-tea-800">{avgRating}<span className="text-sm text-milk-tea-400">/5</span></p>
             </div>
         </div>
      </header>

      {/* Brand Chart */}
      <section className="bg-white p-6 rounded-3xl shadow-sm border border-milk-tea-100">
        <h3 className="text-lg font-bold text-milk-tea-900 mb-4">Brand Favorites</h3>
        <div className="h-64 w-full">
            {brandData.length > 0 ? (
                <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                    <Pie
                        data={brandData}
                        cx="50%"
                        cy="50%"
                        innerRadius={60}
                        outerRadius={80}
                        paddingAngle={5}
                        dataKey="value"
                    >
                        {brandData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                        ))}
                    </Pie>
                    <Tooltip 
                        contentStyle={{ borderRadius: '12px', border: 'none', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}
                    />
                    </PieChart>
                </ResponsiveContainer>
            ) : (
                <div className="h-full flex items-center justify-center text-milk-tea-300">No data yet</div>
            )}
        </div>
        <div className="flex flex-wrap gap-2 justify-center mt-2">
            {brandData.map((b, i) => (
                <div key={b.name} className="flex items-center gap-1 text-xs text-milk-tea-600">
                    <span className="w-2 h-2 rounded-full" style={{backgroundColor: COLORS[i % COLORS.length]}} />
                    {b.name}
                </div>
            ))}
        </div>
      </section>

      {/* Weekly Frequency */}
      <section className="bg-white p-6 rounded-3xl shadow-sm border border-milk-tea-100">
        <h3 className="text-lg font-bold text-milk-tea-900 mb-4">This Week</h3>
        <div className="h-48 w-full">
             <ResponsiveContainer width="100%" height="100%">
                <BarChart data={last7Days}>
                    <XAxis 
                        dataKey="name" 
                        axisLine={false} 
                        tickLine={false} 
                        tick={{fill: '#9CA3AF', fontSize: 10}} 
