import React, { useMemo, useState } from 'react';
import { TeaRecord } from '../types';
import { Search, X } from 'lucide-react';

interface LibraryViewProps {
  records: TeaRecord[];
}

export const LibraryView: React.FC<LibraryViewProps> = ({ records }) => {
  const [searchQuery, setSearchQuery] = useState('');

  // First filter records based on search query
  const filteredRecords = useMemo(() => {
    if (!searchQuery.trim()) return records;
    const lowerQuery = searchQuery.toLowerCase();
    return records.filter(
      r => 
        r.brand.toLowerCase().includes(lowerQuery) || 
        r.name.toLowerCase().includes(lowerQuery)
    );
  }, [records, searchQuery]);

  // Then group the filtered records
  const groupedByBrand = useMemo(() => {
    const groups: Record<string, TeaRecord[]> = {};
    filteredRecords.forEach(r => {
      if (!groups[r.brand]) groups[r.brand] = [];
      groups[r.brand].push(r);
    });
    return groups;
  }, [filteredRecords]);

  // Calculate generic stats per brand
  const getBrandStats = (items: TeaRecord[]) => {
    const avg = items.reduce((a, b) => a + b.rating, 0) / items.length;
    return { count: items.length, avg: avg.toFixed(1) };
  };

  return (
    <div className="p-6 pb-24 max-w-md mx-auto animate-fade-in">
      <div className="sticky top-0 bg-cream/95 backdrop-blur-sm z-10 pb-4 -mx-6 px-6 pt-2">
        <h1 className="text-3xl font-bold text-milk-tea-900 mb-4">Tasting Log</h1>

        {/* Search Bar */}
        <div className="relative">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-milk-tea-300" size={20} />
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search brand or drink..."
            className="w-full pl-12 pr-10 py-3 bg-white rounded-2xl border border-milk-tea-100 text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-200 placeholder:text-milk-tea-200 transition-all shadow-sm"
          />
          {searchQuery && (
            <button 
              onClick={() => setSearchQuery('')}
              className="absolute right-3 top-1/2 -translate-y-1/2 text-milk-tea-300 hover:text-milk-tea-500 p-1"
            >
              <X size={16} />
            </button>
          )}
        </div>
      </div>

      <div className="space-y-6 mt-2">
        {(Object.entries(groupedByBrand) as [string, TeaRecord[]][]).map(([brand, items]) => {
            const stats = getBrandStats(items);
            return (
                <div key={brand} className="bg-white rounded-3xl p-5 shadow-sm border border-milk-tea-100">
                    <div className="flex justify-between items-center mb-4 border-b border-milk-tea-50 pb-2">
                        <div className="flex items-center gap-3">
                             <div className="w-10 h-10 rounded-full bg-milk-tea-200 flex items-center justify-center text-milk-tea-800 font-bold text-lg">
                                {brand[0]}
                             </div>
                             <div>
                                <h2 className="font-bold text-milk-tea-900 text-lg">{brand}</h2>
                                <p className="text-xs text-milk-tea-400">{stats.count} drinks • {stats.avg} avg rating</p>
                             </div>
                        </div>
                    </div>

                    <div className="space-y-3">
                        {items.map(item => (
                            <div key={item.id} className="flex justify-between items-center text-sm group">
                                <div className="flex-1">
                                    <p className="font-medium text-milk-tea-800">{item.name}</p>
                                    <p className="text-[10px] text-milk-tea-400">
                                        {item.sugar !== 'Regular' ? item.sugar : ''} {item.ice !== 'Regular' ? item.ice : ''}
                                        {(item.sugar === 'Regular' && item.ice === 'Regular') ? 'Standard' : ''}
                                    </p>
                                </div>
                                <div className="flex items-center gap-2">
                                     <span className="text-yellow-400 text-xs font-bold">★ {item.rating}</span>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )
        })}

        {records.length > 0 && filteredRecords.length === 0 && (
             <div className="text-center text-milk-tea-300 mt-10">
                <p>No matches found for "{searchQuery}"</p>
            </div>
        )}

        {records.length === 0 && (
            <p className="text-center text-milk-tea-300 mt-10">No records found. Start drinking!</p>
        )}
      </div>
    </div>
  );
};

export interface TeaRecord {
  id: string;
  brand: string;
  name: string;
  sugar: string; // e.g., "30%", "Regular", "Zero"
  ice: string;   // e.g., "None", "Less", "Regular"
  price: number;
  rating: number; // 1-5
  remark: string;
  timestamp: number;
  imageUrl?: string; // base64 or url
  tags?: string[]; // Toppings or custom tags
}

export type Screen = 'calendar' | 'record' | 'stats' | 'library';

export interface BrandStat {
  name: string;
  count: number;
  avgRating: number;
}

export interface DayStat {
  date: string;
  count: number;
}

import React, { useEffect, useState } from 'react';
import { Screen, TeaRecord } from './types';
import { loadRecords, saveRecords, seedInitialData } from './services/storageService';
import { Navigation } from './components/Navigation';
import { CalendarView } from './components/CalendarView';
import { RecordForm } from './components/RecordForm';
import { StatsView } from './components/StatsView';
import { LibraryView } from './components/LibraryView';

export default function App() {
  const [screen, setScreen] = useState<Screen>('calendar');
  const [records, setRecords] = useState<TeaRecord[]>([]);
  const [editingRecord, setEditingRecord] = useState<TeaRecord | undefined>(undefined);

  // Initialize Data
  useEffect(() => {
    const data = seedInitialData();
    setRecords(data);
  }, []);

  const handleSaveRecord = (newRecordData: Omit<TeaRecord, 'id' | 'timestamp'>) => {
    let updatedRecords;

    if (editingRecord) {
      // Update existing
      updatedRecords = records.map(r => 
        r.id === editingRecord.id 
          ? { ...newRecordData, id: editingRecord.id, timestamp: editingRecord.timestamp } 
          : r
      );
      setEditingRecord(undefined);
    } else {
      // Create new
      const record: TeaRecord = {
        ...newRecordData,
        id: crypto.randomUUID(),
        timestamp: Date.now(),
      };
      updatedRecords = [...records, record];
    }

    setRecords(updatedRecords);
    saveRecords(updatedRecords);
    setScreen('calendar');
  };

  const handleDeleteRecord = (id: string) => {
    if (confirm('Are you sure you want to delete this record?')) {
      const updated = records.filter(r => r.id !== id);
      setRecords(updated);
      saveRecords(updated);
    }
  };

  const handleEditRecord = (record: TeaRecord) => {
    setEditingRecord(record);
    setScreen('record');
  };

  const handleCancelRecord = () => {
    setEditingRecord(undefined);
    setScreen('calendar');
  };

  const renderScreen = () => {
    switch (screen) {
      case 'calendar':
        return <CalendarView records={records} onDelete={handleDeleteRecord} onEdit={handleEditRecord} />;
      case 'record':
        // Navigation handles showing form overlay or navigating
        return <CalendarView records={records} onDelete={handleDeleteRecord} onEdit={handleEditRecord} />; 
      case 'stats':
        return <StatsView records={records} />;
      case 'library':
        return <LibraryView records={records} />;
      default:
        return <CalendarView records={records} onDelete={handleDeleteRecord} onEdit={handleEditRecord} />;
    }
  };

  return (
    <div className="min-h-screen bg-cream relative max-w-lg mx-auto shadow-2xl overflow-hidden font-sans">

      {/* Main Content Area */}
      <main className="h-full overflow-y-auto no-scrollbar pb-32">
         {renderScreen()}
      </main>

      {/* Record Overlay Modal */}
      {screen === 'record' && (
        <div className="fixed inset-0 z-50 bg-cream animate-slide-up overflow-y-auto">
            <RecordForm 
                initialData={editingRecord}
                onSave={handleSaveRecord} 
                onCancel={handleCancelRecord} 
            />
        </div>
      )}

      {/* Navigation - Hide when in record mode */}
      {screen !== 'record' && (
        <Navigation currentScreen={screen} onNavigate={setScreen} />
      )}

      <style>{`
        @keyframes fade-in {
            from { opacity: 0; transform: translateY(10px); }
            to { opacity: 1; transform: translateY(0); }
        }
        @keyframes slide-up {
            from { transform: translateY(100%); }
            to { transform: translateY(0); }
        }
        .animate-fade-in {
            animation: fade-in 0.4s ease-out forwards;
        }
        .animate-slide-up {
            animation: slide-up 0.3s cubic-bezier(0.16, 1, 0.3, 1) forwards;
        }
      `}</style>
    </div>
  );
}

