            {/* Remark */}
            <div className="space-y-1">
                <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Remark</label>
                <textarea
                    value={formData.remark}
                    onChange={(e) => setFormData({...formData, remark: e.target.value})}
                    placeholder="How was the texture? Flavor?"
                    rows={3}
                    className="w-full p-4 bg-white border border-milk-tea-100 rounded-2xl focus:outline-none focus:ring-2 focus:ring-milk-tea-300 text-milk-tea-900 resize-none placeholder:text-milk-tea-200"
                />
            </div>
        </div>
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { TeaRecord } from '../types';
import { ChevronLeft, ChevronRight, Edit2, Trash2, Search, X, ChevronDown } from 'lucide-react';

interface CalendarViewProps {
  records: TeaRecord[];
  onDelete?: (id: string) => void;
  onEdit?: (record: TeaRecord) => void;
}

export const CalendarView: React.FC<CalendarViewProps> = ({ records, onDelete, onEdit }) => {
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [searchQuery, setSearchQuery] = useState('');

  // Month navigation
  const prevMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  const nextMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));

  const handleMonthChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.value) {
        const [y, m] = e.target.value.split('-');
        setCurrentMonth(new Date(parseInt(y), parseInt(m) - 1, 1));
    }
  };

  const getDaysInMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
  };

  const daysInMonth = getDaysInMonth(currentMonth);
  const firstDay = getFirstDayOfMonth(currentMonth);

  // Filter records
  const filteredRecords = records.filter(record => {
    const d = new Date(record.timestamp);
    const matchesMonth = d.getMonth() === currentMonth.getMonth() && d.getFullYear() === currentMonth.getFullYear();
    const matchesDate = selectedDate ? d.getDate() === selectedDate.getDate() : true;
    const matchesSearch = searchQuery 
        ? record.name.toLowerCase().includes(searchQuery.toLowerCase()) || record.brand.toLowerCase().includes(searchQuery.toLowerCase())
        : true;

    // Logic: 
    // If searching, ignore date/month filters (global search).
    // If NOT searching, respect month.
    // If selectedDate is set, respect it.

    if (searchQuery) return matchesSearch;
    return matchesMonth && matchesDate;
  }).sort((a,b) => b.timestamp - a.timestamp);

  // Group records for calendar dots (visual only)
  const recordsByDay = records.reduce((acc, record) => {
    const d = new Date(record.timestamp);
    if (d.getMonth() === currentMonth.getMonth() && d.getFullYear() === currentMonth.getFullYear()) {
      const day = d.getDate();
      if (!acc[day]) acc[day] = [];
      acc[day].push(record);
    }
    return acc;
  }, {} as Record<number, TeaRecord[]>);

  const renderStars = (rating: number) => {
    return "★".repeat(rating) + "☆".repeat(5 - rating);
  };

  const monthInputValue = `${currentMonth.getFullYear()}-${String(currentMonth.getMonth() + 1).padStart(2, '0')}`;

  return (
    <div className="p-6 max-w-md mx-auto animate-fade-in">
        <header className="mb-6 space-y-4">
            <h1 className="text-3xl font-bold text-milk-tea-900">Tea Journal</h1>

            {/* Search Bar */}
            <div className="relative">
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-milk-tea-300" size={18} />
                <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    placeholder="Search your history..."
                    className="w-full pl-10 pr-4 py-2.5 bg-white rounded-xl border border-milk-tea-100 text-sm text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-200 placeholder:text-milk-tea-200 transition-all shadow-sm"
                />
                {searchQuery && (
                    <button 
                    onClick={() => setSearchQuery('')}
                    className="absolute right-3 top-1/2 -translate-y-1/2 text-milk-tea-300 hover:text-milk-tea-500 p-1"
                    >
                    <X size={14} />
                    </button>
                )}
            </div>

            {/* Month Nav - Hidden if searching */}
            {!searchQuery && (
                <div className="flex justify-between items-center bg-white/50 p-2 rounded-2xl backdrop-blur-sm">
                    <button onClick={prevMonth} className="p-2 text-milk-tea-600 hover:bg-white rounded-xl transition-colors">
                        <ChevronLeft size={20} />
                    </button>

                    <div className="relative group flex items-center gap-2 px-3 py-1.5 bg-milk-tea-50/50 rounded-full cursor-pointer hover:bg-milk-tea-100 transition-colors">
                        <span className="text-milk-tea-800 font-bold text-sm uppercase tracking-widest">
                            {currentMonth.toLocaleString('default', { month: 'long', year: 'numeric' })}
                        </span>
                        <ChevronDown size={16} className="text-milk-tea-400" />

                        {/* Native Month Picker Overlay */}
                        <input 
                            type="month" 
                            value={monthInputValue}
                            onChange={handleMonthChange}
                            className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
                        />
                    </div>

                    <button onClick={nextMonth} className="p-2 text-milk-tea-600 hover:bg-white rounded-xl transition-colors">
                        <ChevronRight size={20} />
                    </button>
                </div>
            )}
        </header>

      {/* Calendar Grid - Hidden if searching */}
      {!searchQuery && (
          <div className="bg-white rounded-3xl p-6 shadow-sm shadow-milk-tea-100 mb-8 transition-all">
            <div className="grid grid-cols-7 gap-2 mb-4">
              {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map(d => (
                <div key={d} className="text-center text-xs text-milk-tea-400 font-bold">{d}</div>
              ))}
            </div>
            <div className="grid grid-cols-7 gap-y-4 gap-x-2">
              {Array.from({ length: firstDay }).map((_, i) => (
                <div key={`empty-${i}`} />
              ))}
              {Array.from({ length: daysInMonth }).map((_, i) => {
                const day = i + 1;
                const hasTea = recordsByDay[day]?.length > 0;
                const isSelected = selectedDate?.getDate() === day && selectedDate?.getMonth() === currentMonth.getMonth();

                return (
                  <button 
                    key={day} 
                    onClick={() => {
                        // Toggle selection
                        if (isSelected) setSelectedDate(null);
                        else {
                            const newDate = new Date(currentMonth);
                            newDate.setDate(day);
                            setSelectedDate(newDate);
                        }
                    }}
                    className="flex flex-col items-center gap-1 group relative"
                  >
                    <span className={`text-sm font-medium w-8 h-8 flex items-center justify-center rounded-full transition-all ${
                        isSelected 
                            ? 'bg-milk-tea-800 text-white shadow-md' 
                            : hasTea 
                                ? 'bg-milk-tea-100 text-milk-tea-800 group-hover:bg-milk-tea-200' 
                                : 'text-milk-tea-300 group-hover:bg-cream'
                    }`}>
                      {day}
                    </span>
                    {hasTea && !isSelected && (
                        <div className="flex gap-0.5 absolute -bottom-1.5">
                            {recordsByDay[day].slice(0, 3).map((r, idx) => (
                                <div key={idx} className={`w-1 h-1 rounded-full ${r.rating >= 4 ? 'bg-milk-tea-800' : 'bg-milk-tea-400'}`} />
                            ))}
                        </div>
                    )}
                  </button>
                );
              })}
            </div>
          </div>
      )}

      {/* Filtered Records List */}
      <div className="flex justify-between items-center mb-4 px-1">
          <h3 className="text-lg font-bold text-milk-tea-800">
            {searchQuery ? 'Search Results' : selectedDate ? 'Daily Sips' : 'Recent Sips'}
          </h3>
          {selectedDate && !searchQuery && (
              <button onClick={() => setSelectedDate(null)} className="text-xs text-milk-tea-500 hover:text-milk-tea-800">Clear Date</button>
          )}
      </div>

      <div className="space-y-4 pb-24">
        {filteredRecords.map(record => (
          <div key={record.id} className="bg-white p-4 rounded-2xl flex gap-4 shadow-sm shadow-milk-tea-100 border border-transparent hover:border-milk-tea-200 transition-colors group relative">
            <div className="w-16 h-16 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative">
                {record.imageUrl ? (
                    <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                ) : (
                    <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-2xl font-bold opacity-50">
                        {record.brand[0]}
                    </div>
                )}
            </div>
            <div className="flex-1 min-w-0">
              <div className="flex justify-between items-start">
                <div>
                    <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                    <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                </div>
                <div className="flex flex-col items-end">
                    <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
                    <span className="text-xs font-bold text-milk-tea-800 mt-1">¥{record.price}</span>
                </div>
              </div>

              <div className="mt-2 flex flex-wrap gap-2">
                <span className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                    {record.sugar || 'Regular'}
                </span>
                {record.tags && record.tags.map((tag, i) => (
                    <span key={i} className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                        {tag}
                    </span>
                ))}
              </div>
            </div>

            {/* Action Buttons (Visible on hover or consistent on mobile) */}
            <div className="absolute top-2 right-2 flex gap-2 opacity-0 group-hover:opacity-100 transition-opacity bg-white/80 backdrop-blur rounded-lg p-1">
                <button onClick={() => onEdit?.(record)} className="p-1.5 text-milk-tea-500 hover:text-milk-tea-800 hover:bg-milk-tea-50 rounded-md">
                    <Edit2 size={14} />
                </button>
                <button onClick={() => onDelete?.(record.id)} className="p-1.5 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-md">
                    <Trash2 size={14} />
                </button>
            </div>
          </div>
        ))}
        {filteredRecords.length === 0 && (
            <div className="text-center py-12 text-milk-tea-300">
                <p>{searchQuery ? 'No matches found.' : 'No bubble tea recorded.'}</p>
            </div>
        )}
      </div>
    </div>
  );
};

import React, { useState, useRef, useEffect } from 'react';
import { Camera, X, Loader2, Sparkles } from 'lucide-react';
import { TeaRecord } from '../types';
import { analyzeTeaImage } from '../services/geminiService';

interface RecordFormProps {
  initialData?: TeaRecord;
  onSave: (record: Omit<TeaRecord, 'id' | 'timestamp'>) => void;
  onCancel: () => void;
}

const COMMON_SUGAR = ['Regular', '70%', '50%', '30%', 'Zero'];
const COMMON_ICE = ['Regular', 'Less', 'No Ice', 'Hot'];

export const RecordForm: React.FC<RecordFormProps> = ({ initialData, onSave, onCancel }) => {
  const [loading, setLoading] = useState(false);
  const [imagePreview, setImagePreview] = useState<string | null>(initialData?.imageUrl || null);

  const [formData, setFormData] = useState({
    brand: initialData?.brand || '',
    name: initialData?.name || '',
    sugar: initialData?.sugar || '',
    ice: initialData?.ice || '',
    price: initialData?.price ? String(initialData.price) : '',
    rating: initialData?.rating || 0,
    remark: initialData?.remark || '',
  });

  // Tag/Topping state
  const [currentTag, setCurrentTag] = useState('');
  const [tags, setTags] = useState<string[]>(initialData?.tags || []);

  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = async () => {
      const base64 = reader.result as string;
      setImagePreview(base64);

      // Auto-analyze
      setLoading(true);
      try {
        const info = await analyzeTeaImage(base64);
        setFormData(prev => ({
            ...prev,
            brand: info.brand || prev.brand,
            name: info.name || prev.name,
            sugar: info.sugar || prev.sugar,
            ice: info.ice || prev.ice,
            price: info.price ? String(info.price) : prev.price
        }));
      } catch (err) {
        console.error("AI Analysis failed, manual entry required");
      } finally {
        setLoading(false);
      }
    };
    reader.readAsDataURL(file);
  };

  const handleTagKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
        e.preventDefault();
        if (currentTag.trim()) {
            setTags([...tags, currentTag.trim()]);
            setCurrentTag('');
        }
    }
  };

  const removeTag = (index: number) => {
    setTags(tags.filter((_, i) => i !== index));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSave({
      brand: formData.brand || 'Unknown Brand',
      name: formData.name || 'Unknown Drink',
      sugar: formData.sugar,
      ice: formData.ice,
      price: parseFloat(formData.price) || 0,
      rating: formData.rating,
      remark: formData.remark,
      imageUrl: imagePreview || undefined,
      tags: tags
    });
  };

  return (
    <div className="bg-cream min-h-full pb-6 animate-fade-in">
      <div className="sticky top-0 bg-cream/95 backdrop-blur z-20 px-6 py-4 flex justify-between items-center border-b border-milk-tea-100">
        <button onClick={onCancel} className="p-2 -ml-2 text-milk-tea-500">
            <X size={24} />
        </button>
        <h2 className="text-lg font-bold text-milk-tea-900">{initialData ? 'Edit Poke' : 'New Poke'}</h2>
        <button 
            onClick={handleSubmit}
            className="px-4 py-1.5 bg-milk-tea-800 text-cream rounded-full text-sm font-semibold shadow-md active:scale-95 transition-transform"
        >
            Save
        </button>
      </div>

      <div className="p-6 space-y-6 max-w-md mx-auto">
        {/* Image Section */}
        <div className="relative group">
            <input 
                type="file" 
                ref={fileInputRef} 
                onChange={handleFileChange} 
                accept="image/*" 
                className="hidden" 
            />

            <div 
                onClick={() => fileInputRef.current?.click()}
                className={`w-full aspect-[4/3] rounded-3xl border-2 border-dashed flex flex-col items-center justify-center cursor-pointer transition-all overflow-hidden ${
                    imagePreview ? 'border-transparent' : 'border-milk-tea-200 bg-milk-tea-100/30 hover:bg-milk-tea-100/50'
                }`}
            >
                {imagePreview ? (
                    <>
                        <img src={imagePreview} alt="Preview" className="w-full h-full object-cover" />
                        <div className="absolute inset-0 bg-black/20 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                            <span className="text-white text-sm font-medium">Change Photo</span>
                        </div>
                    </>
                ) : (
                    <div className="text-milk-tea-400 flex flex-col items-center gap-2">
                        <Camera size={32} />
                        <span className="text-sm">Tap to scan cup/receipt</span>
                    </div>
                )}
            </div>

            {loading && (
                <div className="absolute inset-0 bg-cream/80 flex flex-col items-center justify-center rounded-3xl z-10 backdrop-blur-sm">
                    <Loader2 className="animate-spin text-milk-tea-800 mb-2" size={32} />
                    <p className="text-milk-tea-800 text-sm font-medium animate-pulse flex items-center gap-1">
                        <Sparkles size={14} /> AI Identifying...
                    </p>
                </div>
            )}
        </div>

        {/* Form Fields */}
        <div className="space-y-5">
            {/* Brand & Name */}
            <div className="space-y-3">
                <div className="space-y-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Brand</label>
                    <input
                        type="text"
                        value={formData.brand}
                        onChange={(e) => setFormData({...formData, brand: e.target.value})}
                        placeholder="e.g. HeyTea"
                        className="w-full p-4 bg-white border border-milk-tea-100 rounded-2xl focus:outline-none focus:ring-2 focus:ring-milk-tea-300 text-milk-tea-900 placeholder:text-milk-tea-200"
                    />
                </div>
                <div className="space-y-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Drink Name</label>
                    <input
                        type="text"
                        value={formData.name}
                        onChange={(e) => setFormData({...formData, name: e.target.value})}
                        placeholder="e.g. Grape Cheezo"
                        className="w-full p-4 bg-white border border-milk-tea-100 rounded-2xl focus:outline-none focus:ring-2 focus:ring-milk-tea-300 text-milk-tea-900 placeholder:text-milk-tea-200"
                    />
                </div>
            </div>

            {/* Toppings (Tags) */}
            <div className="space-y-1">
                <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Toppings</label>
                <div className="w-full p-2 bg-white border border-milk-tea-100 rounded-2xl focus-within:ring-2 focus-within:ring-milk-tea-300 min-h-[56px] flex flex-wrap gap-2 items-center">
                    {tags.map((tag, idx) => (
                        <span key={idx} className="bg-milk-tea-100 text-milk-tea-800 px-3 py-1 rounded-full text-sm font-medium flex items-center gap-1">
                            {tag}
                            <button type="button" onClick={() => removeTag(idx)} className="hover:text-milk-tea-900">
                                <X size={12} />
                            </button>
                        </span>
                    ))}
                    <input
                        type="text"
                        value={currentTag}
                        onChange={(e) => setCurrentTag(e.target.value)}
                        onKeyDown={handleTagKeyDown}
                        placeholder={tags.length === 0 ? "e.g. Boba (Press Enter)" : ""}
                        className="flex-1 bg-transparent p-2 focus:outline-none text-milk-tea-900 min-w-[100px]"
                    />
                </div>
            </div>

            {/* Options Row */}
            <div className="grid grid-cols-2 gap-4">
                <div className="space-y-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Sugar</label>
                     <div className="relative">
                        <select 
                            value={formData.sugar}
                            onChange={(e) => setFormData({...formData, sugar: e.target.value})}
                            className="w-full p-3 bg-white border border-milk-tea-100 rounded-xl appearance-none text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-300"
                        >
                            <option value="">Select</option>
                            {COMMON_SUGAR.map(s => <option key={s} value={s}>{s}</option>)}
                        </select>
                        <div className="absolute right-3 top-1/2 -translate-y-1/2 pointer-events-none text-milk-tea-400">▼</div>
                     </div>
                </div>
                <div className="space-y-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Ice</label>
                    <div className="relative">
                        <select 
                            value={formData.ice}
                            onChange={(e) => setFormData({...formData, ice: e.target.value})}
                            className="w-full p-3 bg-white border border-milk-tea-100 rounded-xl appearance-none text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-300"
                        >
                            <option value="">Select</option>
                            {COMMON_ICE.map(s => <option key={s} value={s}>{s}</option>)}
                        </select>
                        <div className="absolute right-3 top-1/2 -translate-y-1/2 pointer-events-none text-milk-tea-400">▼</div>
                    </div>
                </div>
            </div>

            {/* Price & Rating */}
            <div className="flex gap-4 items-end">
                <div className="space-y-1 flex-1">
                    <label className="text-xs font-bold text-milk-tea-500 uppercase tracking-wider ml-1">Price</label>
                    <input
                        type="number"
                        value={formData.price}
                        onChange={(e) => setFormData({...formData, price: e.target.value})}
                        placeholder="0.00"
                        className="w-full p-3 bg-white border border-milk-tea-100 rounded-xl focus:outline-none focus:ring-2 focus:ring-milk-tea-300 text-milk-tea-900 text-center"
                    />
