            {/* Action Buttons (Visible on hover or consistent on mobile) */}
            <div className="absolute top-2 right-2 flex gap-2 opacity-0 group-hover:opacity-100 transition-opacity bg-white/80 backdrop-blur rounded-lg p-1 z-10">
                <button 
                    onClick={(e) => { e.stopPropagation(); onEdit?.(record); }} 
