import React, { useMemo, useState } from 'react';
import { TeaRecord } from '../types';
import { Search, X, ChevronDown, ChevronRight, CupSoda } from 'lucide-react';

interface LibraryViewProps {
  records: TeaRecord[];
}

interface DrinkGroup {
    name: string;
    records: TeaRecord[];
    count: number;
    avgRating: number;
    latestTimestamp: number;
}

interface BrandGroup {
    brand: string;
    drinks: DrinkGroup[];
    totalCount: number;
    avgRating: number;
}

export const LibraryView: React.FC<LibraryViewProps> = ({ records }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [expandedBrand, setExpandedBrand] = useState<string | null>(null);

  // Grouping Logic
  const groupedData: BrandGroup[] = useMemo(() => {
    if (searchQuery) return []; 

    const brands: Record<string, Record<string, TeaRecord[]>> = {};

    // 1. Group by Brand -> Drink Name
    records.forEach(r => {
        if (!brands[r.brand]) brands[r.brand] = {};
        if (!brands[r.brand][r.name]) brands[r.brand][r.name] = [];
        brands[r.brand][r.name].push(r);
    });

    // 2. Transform to Array structure
    const brandGroups: BrandGroup[] = Object.entries(brands).map(([brandName, drinkMap]) => {
        const drinks: DrinkGroup[] = Object.entries(drinkMap).map(([drinkName, drinkRecords]) => {
            const sumRating = drinkRecords.reduce((sum, r) => sum + r.rating, 0);
            const latest = Math.max(...drinkRecords.map(r => r.timestamp));

            return {
                name: drinkName,
                records: drinkRecords,
                count: drinkRecords.length,
                avgRating: sumRating / drinkRecords.length,
                latestTimestamp: latest
            };
        });

        // Sort Drinks: Frequency (Desc) -> Rating (Desc) -> Latest Time (Desc)
        drinks.sort((a, b) => {
            if (b.count !== a.count) return b.count - a.count;
            if (b.avgRating !== a.avgRating) return b.avgRating - a.avgRating;
            return b.latestTimestamp - a.latestTimestamp;
        });

        const totalCount = drinks.reduce((sum, d) => sum + d.count, 0);
        // Calculate weighted average for brand? or simple average of all records
        const allBrandRecords = drinks.flatMap(d => d.records);
        const brandAvgRating = allBrandRecords.reduce((sum, r) => sum + r.rating, 0) / allBrandRecords.length;

        return {
            brand: brandName,
            drinks,
            totalCount,
            avgRating: brandAvgRating
        };
    });

    // Sort Brands alphabetically or by count? Let's do alphabetically as default
    brandGroups.sort((a, b) => a.brand.localeCompare(b.brand));

    return brandGroups;
  }, [records, searchQuery]);

  // Search Logic
  const filteredRecords = useMemo(() => {
    if (!searchQuery.trim()) return [];
    const lowerQuery = searchQuery.toLowerCase();
    return records.filter(
      r => 
        r.brand.toLowerCase().includes(lowerQuery) || 
        r.name.toLowerCase().includes(lowerQuery)
    );
  }, [records, searchQuery]);

  const renderStars = (rating: number) => {
    return "★".repeat(Math.round(rating)) + "☆".repeat(5 - Math.round(rating));
  };

  const toggleBrand = (brand: string) => {
      setExpandedBrand(expandedBrand === brand ? null : brand);
  };

  return (
    <div className="p-6 pb-24 max-w-md mx-auto animate-fade-in">
      <div className="sticky top-0 bg-cream/95 backdrop-blur-sm z-10 pb-4 -mx-6 px-6 pt-2">
        <h1 className="text-3xl font-bold text-milk-tea-900 mb-4">Tasting Log</h1>

        {/* Search Bar */}
        <div className="relative mb-3">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-milk-tea-300" size={20} />
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search brand or drink..."
            className="w-full pl-12 pr-10 py-3 bg-white rounded-2xl border border-milk-tea-100 text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-200 placeholder:text-milk-tea-200 transition-all shadow-sm"
          />
          {searchQuery && (
            <button 
              onClick={() => setSearchQuery('')}
              className="absolute right-3 top-1/2 -translate-y-1/2 text-milk-tea-300 hover:text-milk-tea-500 p-1"
            >
              <X size={16} />
            </button>
          )}
        </div>
      </div>

      <div className="space-y-4 mt-2">
        {/* Accordion Brand List */}
        {!searchQuery && groupedData.map((group) => {
            const isExpanded = expandedBrand === group.brand;
            return (
                <div key={group.brand} className="bg-white rounded-3xl overflow-hidden shadow-sm border border-milk-tea-100 transition-all duration-300">
                    <button 
                        onClick={() => toggleBrand(group.brand)}
                        className={`w-full flex justify-between items-center p-5 text-left transition-colors ${isExpanded ? 'bg-milk-tea-50/50' : 'hover:bg-milk-tea-50/30'}`}
                    >
                        <div className="flex items-center gap-4">
                             <div className="w-12 h-12 rounded-full bg-milk-tea-100 flex items-center justify-center text-milk-tea-800 font-bold text-xl">
                                {group.brand[0]}
                             </div>
                             <div>
                                <h2 className="font-bold text-milk-tea-900 text-lg">{group.brand}</h2>
                                <p className="text-xs text-milk-tea-500 font-medium">
                                    {group.totalCount} drinks • {group.avgRating.toFixed(1)} avg
                                </p>
                             </div>
                        </div>
                        <div className={`text-milk-tea-400 transition-transform duration-300 ${isExpanded ? 'rotate-180' : ''}`}>
                            <ChevronDown size={20} />
                        </div>
                    </button>

                    {/* Expanded Content */}
                    {isExpanded && (
                        <div className="bg-white border-t border-milk-tea-50 animate-fade-in">
                            {group.drinks.map((drink, idx) => (
                                <div key={`${drink.name}-${idx}`} className="p-4 flex items-center justify-between border-b border-milk-tea-50 last:border-none hover:bg-cream/50 transition-colors">
                                    <div className="flex items-start gap-3">
                                        <div className="mt-1 text-milk-tea-300">
                                            <CupSoda size={16} />
                                        </div>
                                        <div>
                                            <p className="font-bold text-milk-tea-800 text-sm">{drink.name}</p>
                                            <div className="flex gap-2 mt-1">
                                                <span className="px-1.5 py-0.5 bg-milk-tea-100 text-milk-tea-800 text-[10px] rounded font-medium">
                                                    {drink.count} cups
                                                </span>
                                                <span className="text-[10px] text-milk-tea-400 flex items-center">
                                                    Last: {new Date(drink.latestTimestamp).toLocaleDateString()}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="text-right">
                                        <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(drink.avgRating)}</span>
                                        <p className="text-[10px] text-milk-tea-400 font-medium mt-0.5">
                                            {drink.avgRating.toFixed(1)}
                                        </p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            )
        })}

        {/* Search Results (Flat List) */}
        {searchQuery && filteredRecords.map(record => (
             <div key={record.id} className="bg-white p-4 rounded-2xl flex gap-4 shadow-sm shadow-milk-tea-100 border border-transparent">
                <div className="w-14 h-14 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative">
                    {record.imageUrl ? (
                        <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                    ) : (
                        <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-xl font-bold opacity-50">
                            {record.brand[0]}
                        </div>
                    )}
                </div>
                <div className="flex-1 min-w-0">
                    <div className="flex justify-between items-start">
                        <div>
                            <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                            <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                        </div>
                        <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
                    </div>
                    <div className="mt-1 flex flex-wrap gap-2">
                         <p className="text-[11px] text-milk-tea-400">
                             {record.sugar} • {record.ice}
                         </p>
                    </div>
                </div>
             </div>
        ))}

        {records.length > 0 && searchQuery && filteredRecords.length === 0 && (
             <div className="text-center text-milk-tea-300 mt-10">
                <p>No matches found for "{searchQuery}"</p>
            </div>
        )}

        {records.length === 0 && (
            <div className="text-center text-milk-tea-300 mt-20 flex flex-col items-center">
                <p>Your library is empty.</p>
                <p className="text-sm">Start drinking!</p>
            </div>
        )}
      </div>
    </div>
  );
};

import React, { useEffect, useState } from 'react';
import { Screen, TeaRecord } from './types';
import { loadRecords, saveRecords, seedInitialData } from './services/storageService';
import { Navigation } from './components/Navigation';
import { CalendarView } from './components/CalendarView';
import { RecordForm } from './components/RecordForm';
import { StatsView } from './components/StatsView';
import { LibraryView } from './components/LibraryView';
import { RecordDetailModal } from './components/RecordDetailModal';

export default function App() {
  const [screen, setScreen] = useState<Screen>('calendar');
  const [records, setRecords] = useState<TeaRecord[]>([]);
  const [editingRecord, setEditingRecord] = useState<TeaRecord | undefined>(undefined);
  const [viewingRecord, setViewingRecord] = useState<TeaRecord | null>(null);

  // Initialize Data
  useEffect(() => {
    const data = seedInitialData();
    setRecords(data);
  }, []);

  const handleSaveRecord = (newRecordData: Omit<TeaRecord, 'id' | 'timestamp'>) => {
    let updatedRecords;

    if (editingRecord) {
      // Update existing
      updatedRecords = records.map(r => 
        r.id === editingRecord.id 
          ? { ...newRecordData, id: editingRecord.id, timestamp: editingRecord.timestamp } 
          : r
      );
      setEditingRecord(undefined);
    } else {
      // Create new
      const record: TeaRecord = {
        ...newRecordData,
        id: crypto.randomUUID(),
        timestamp: Date.now(),
      };
      updatedRecords = [...records, record];
    }

    setRecords(updatedRecords);
    saveRecords(updatedRecords);
    setScreen('calendar');
  };

  const handleDeleteRecord = (id: string) => {
    // Basic window.confirm works, but ensure event propagation is stopped in the child component
    if (window.confirm('Are you sure you want to delete this record?')) {
      const updated = records.filter(r => r.id !== id);
      setRecords(updated);
      saveRecords(updated);
      // Close detail view if deleting the currently viewed record
      if (viewingRecord?.id === id) {
          setViewingRecord(null);
      }
    }
  };

  const handleEditRecord = (record: TeaRecord) => {
    setEditingRecord(record);
    setScreen('record');
    setViewingRecord(null); // Close detail view if open
  };

  const handleCancelRecord = () => {
    setEditingRecord(undefined);
    setScreen('calendar');
  };

  const renderScreen = () => {
    switch (screen) {
      case 'calendar':
        return <CalendarView records={records} onDelete={handleDeleteRecord} onEdit={handleEditRecord} onViewRecord={setViewingRecord} />;
      case 'record':
        // Navigation handles showing form overlay or navigating
        return <CalendarView records={records} onDelete={handleDeleteRecord} onEdit={handleEditRecord} />; 
      case 'stats':
        return <StatsView records={records} />;
      case 'library':
        return <LibraryView records={records} />;
      default:
        return <CalendarView records={records} onDelete={handleDeleteRecord} onEdit={handleEditRecord} onViewRecord={setViewingRecord} />;
    }
  };

  return (
    <div className="min-h-screen bg-cream relative max-w-lg mx-auto shadow-2xl overflow-hidden font-sans">

      {/* Main Content Area */}
      <main className="h-full overflow-y-auto no-scrollbar pb-32">
         {renderScreen()}
      </main>

      {/* Record Overlay Modal (Edit/New) */}
      {screen === 'record' && (
        <div className="fixed inset-0 z-50 bg-cream animate-slide-up overflow-y-auto">
            <RecordForm 
                initialData={editingRecord}
                onSave={handleSaveRecord} 
                onCancel={handleCancelRecord} 
            />
        </div>
      )}

      {/* View Detail Modal (Read Only) */}
      {viewingRecord && (
          <RecordDetailModal 
            record={viewingRecord} 
            onClose={() => setViewingRecord(null)} 
          />
      )}

      {/* Navigation - Hide when in record mode */}
      {screen !== 'record' && (
        <Navigation currentScreen={screen} onNavigate={setScreen} />
      )}

      <style>{`
        @keyframes fade-in {
            from { opacity: 0; transform: translateY(10px); }
            to { opacity: 1; transform: translateY(0); }
        }
        @keyframes slide-up {
            from { transform: translateY(100%); }
            to { transform: translateY(0); }
        }
        .animate-fade-in {
            animation: fade-in 0.4s ease-out forwards;
        }
        .animate-slide-up {
            animation: slide-up 0.3s cubic-bezier(0.16, 1, 0.3, 1) forwards;
        }
      `}</style>
    </div>
  );
}

import React, { useState } from 'react';
import { TeaRecord } from '../types';
import { ChevronLeft, ChevronRight, Edit2, Trash2, Search, X, ChevronDown } from 'lucide-react';

interface CalendarViewProps {
  records: TeaRecord[];
  onDelete?: (id: string) => void;
  onEdit?: (record: TeaRecord) => void;
  onViewRecord?: (record: TeaRecord) => void;
}

export const CalendarView: React.FC<CalendarViewProps> = ({ records, onDelete, onEdit, onViewRecord }) => {
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isDatePickerOpen, setIsDatePickerOpen] = useState(false);

  // Month navigation
  const prevMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  const nextMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));

  // Dropdown data
  const currentYear = new Date().getFullYear();
  const years = Array.from({ length: 11 }, (_, i) => currentYear - 5 + i); // +/- 5 years
  const months = Array.from({ length: 12 }, (_, i) => i);

  const getDaysInMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
  };

  const daysInMonth = getDaysInMonth(currentMonth);
  const firstDay = getFirstDayOfMonth(currentMonth);

  // Filter records
  const filteredRecords = records.filter(record => {
    const d = new Date(record.timestamp);
    const matchesMonth = d.getMonth() === currentMonth.getMonth() && d.getFullYear() === currentMonth.getFullYear();
    const matchesDate = selectedDate ? d.getDate() === selectedDate.getDate() : true;
    const matchesSearch = searchQuery 
        ? record.name.toLowerCase().includes(searchQuery.toLowerCase()) || record.brand.toLowerCase().includes(searchQuery.toLowerCase())
        : true;

    if (searchQuery) return matchesSearch;
    return matchesMonth && matchesDate;
  }).sort((a,b) => b.timestamp - a.timestamp);

  // Group records for calendar dots (visual only)
  const recordsByDay = records.reduce((acc, record) => {
    const d = new Date(record.timestamp);
    if (d.getMonth() === currentMonth.getMonth() && d.getFullYear() === currentMonth.getFullYear()) {
      const day = d.getDate();
      if (!acc[day]) acc[day] = [];
      acc[day].push(record);
    }
    return acc;
  }, {} as Record<number, TeaRecord[]>);

  const renderStars = (rating: number) => {
    return "★".repeat(rating) + "☆".repeat(5 - rating);
  };

  return (
    <div className="p-6 max-w-md mx-auto animate-fade-in">
        <header className="mb-6 space-y-4">
            <h1 className="text-3xl font-bold text-milk-tea-900">Tea Journal</h1>

            {/* Search Bar */}
            <div className="relative z-10">
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-milk-tea-300" size={18} />
                <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    placeholder="Search your history..."
                    className="w-full pl-10 pr-4 py-2.5 bg-white rounded-xl border border-milk-tea-100 text-sm text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-200 placeholder:text-milk-tea-200 transition-all shadow-sm"
                />
                {searchQuery && (
                    <button 
                    onClick={() => setSearchQuery('')}
                    className="absolute right-3 top-1/2 -translate-y-1/2 text-milk-tea-300 hover:text-milk-tea-500 p-1"
                    >
                    <X size={14} />
                    </button>
                )}
            </div>

            {/* Month Nav - Hidden if searching */}
            {!searchQuery && (
                <div className="relative flex items-center justify-center bg-white/50 p-2 rounded-2xl backdrop-blur-sm z-20 min-h-[56px]">
                    <button onClick={prevMonth} className="absolute left-2 p-2 text-milk-tea-600 hover:bg-white rounded-xl transition-colors">
                        <ChevronLeft size={20} />
                    </button>

                    <div className="relative">
                        <button 
                            onClick={() => setIsDatePickerOpen(!isDatePickerOpen)}
                            className="flex items-center gap-2 px-4 py-1.5 bg-milk-tea-50/50 rounded-full cursor-pointer hover:bg-milk-tea-100 transition-colors"
                        >
                            <span className="text-milk-tea-800 font-bold text-sm uppercase tracking-widest">
                                {currentMonth.toLocaleString('default', { month: 'long', year: 'numeric' })}
                            </span>
                            <ChevronDown size={16} className={`text-milk-tea-400 transition-transform duration-300 ${isDatePickerOpen ? 'rotate-180' : ''}`} />
                        </button>

                        {/* Custom Date Picker Popup - Centered */}
                        {isDatePickerOpen && (
                            <>
                                {/* Invisible backdrop to close on outside click */}
                                <div className="fixed inset-0 z-40" onClick={() => setIsDatePickerOpen(false)} />

                                <div className="absolute top-full left-1/2 -translate-x-1/2 mt-2 bg-white/95 backdrop-blur-xl p-3 rounded-2xl shadow-[0_10px_40px_rgba(0,0,0,0.12)] border border-white/50 z-50 flex gap-2 min-w-[240px] animate-fade-in origin-top" onClick={(e) => e.stopPropagation()}>
                                    <div className="relative flex-1">
                                        <select 
                                            value={currentMonth.getFullYear()}
                                            onChange={(e) => {
                                                const newYear = parseInt(e.target.value);
                                                setCurrentMonth(new Date(newYear, currentMonth.getMonth(), 1));
                                            }}
                                            className="w-full p-2 bg-milk-tea-50 rounded-xl text-sm font-bold text-milk-tea-900 focus:outline-none appearance-none text-center hover:bg-milk-tea-100 transition-colors cursor-pointer"
                                        >
                                            {years.map(y => <option key={y} value={y}>{y}</option>)}
                                        </select>
                                        <div className="absolute inset-y-0 right-1 flex items-center pointer-events-none">
                                            <ChevronDown size={12} className="text-milk-tea-400" />
                                        </div>
                                    </div>

                                    <div className="relative flex-1">
                                        <select 
                                            value={currentMonth.getMonth()}
                                            onChange={(e) => {
                                                const newMonth = parseInt(e.target.value);
                                                setCurrentMonth(new Date(currentMonth.getFullYear(), newMonth, 1));
                                            }}
                                            className="w-full p-2 bg-milk-tea-50 rounded-xl text-sm font-bold text-milk-tea-900 focus:outline-none appearance-none text-center hover:bg-milk-tea-100 transition-colors cursor-pointer"
                                        >
                                            {months.map(m => (
                                                <option key={m} value={m}>
                                                    {new Date(2000, m, 1).toLocaleString('default', { month: 'short' })}
                                                </option>
                                            ))}
                                        </select>
                                        <div className="absolute inset-y-0 right-1 flex items-center pointer-events-none">
                                            <ChevronDown size={12} className="text-milk-tea-400" />
                                        </div>
                                    </div>
                                </div>
                            </>
                        )}
                    </div>

                    <button onClick={nextMonth} className="absolute right-2 p-2 text-milk-tea-600 hover:bg-white rounded-xl transition-colors">
                        <ChevronRight size={20} />
                    </button>
                </div>
            )}
        </header>

      {/* Calendar Grid - Hidden if searching */}
      {!searchQuery && (
          <div className="bg-white rounded-3xl p-6 shadow-sm shadow-milk-tea-100 mb-8 transition-all relative z-0">
            <div className="grid grid-cols-7 gap-2 mb-4">
              {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map(d => (
                <div key={d} className="text-center text-xs text-milk-tea-400 font-bold">{d}</div>
              ))}
            </div>
            <div className="grid grid-cols-7 gap-y-4 gap-x-2">
              {Array.from({ length: firstDay }).map((_, i) => (
                <div key={`empty-${i}`} />
              ))}
              {Array.from({ length: daysInMonth }).map((_, i) => {
                const day = i + 1;
                const dailyRecords = recordsByDay[day] || [];
                const count = dailyRecords.length;
                const hasTea = count > 0;
                const isSelected = selectedDate?.getDate() === day && selectedDate?.getMonth() === currentMonth.getMonth();

                // Determine background color based on intensity
                let bgClass = '';
                let textClass = '';

                if (isSelected) {
                   bgClass = 'bg-milk-tea-800 shadow-md scale-110';
                   textClass = 'text-white';
                } else if (count === 0) {
                   bgClass = 'text-milk-tea-300 hover:bg-cream';
                   textClass = 'text-milk-tea-300';
                } else if (count === 1) {
                   bgClass = 'bg-milk-tea-100 hover:bg-milk-tea-200';
                   textClass = 'text-milk-tea-800';
                } else if (count === 2) {
                   bgClass = 'bg-milk-tea-200 hover:bg-[#EBCFBF]';
                   textClass = 'text-milk-tea-900';
                } else {
                   // 3 or more
                   bgClass = 'bg-milk-tea-500 hover:bg-milk-tea-800';
                   textClass = 'text-white';
                }

                return (
                  <button 
                    key={day} 
                    onClick={() => {
                        // Toggle selection
                        if (isSelected) setSelectedDate(null);
                        else {
                            const newDate = new Date(currentMonth);
                            newDate.setDate(day);
                            setSelectedDate(newDate);
                        }
                    }}
                    className="flex flex-col items-center gap-1 group relative"
                  >
                    <span className={`text-sm font-medium w-8 h-8 flex items-center justify-center rounded-full transition-all duration-300 ${bgClass} ${textClass}`}>
                      {day}
                    </span>
                    {/* Dots always visible if hasTea, even if selected, to show density */}
                    {hasTea && (
                        <div className="flex gap-0.5 absolute -bottom-1.5">
                            {/* Display dots - max 4 */}
                            {dailyRecords.slice(0, Math.min(count, 4)).map((r, idx) => {
                                // If background is dark (selected or high count), dots are white. Else brown.
                                const isDarkBg = isSelected || count >= 3;
                                const dotColor = isDarkBg ? 'bg-white/90' : 'bg-milk-tea-800/70';
                                return (
                                    <div key={idx} className={`w-1 h-1 rounded-full ${dotColor}`} />
                                );
                            })}
                            {count > 4 && <div className={`w-1 h-1 rounded-full ${isSelected || count >= 3 ? 'bg-white/90' : 'bg-milk-tea-800/70'}`} />}
                        </div>
                    )}
                  </button>
                );
              })}
            </div>
          </div>
      )}

      {/* Filtered Records List */}
      <div className="flex justify-between items-center mb-4 px-1">
          <h3 className="text-lg font-bold text-milk-tea-800">
            {searchQuery ? 'Search Results' : selectedDate ? 'Daily Sips' : 'Recent Sips'}
          </h3>
          {selectedDate && !searchQuery && (
              <button onClick={() => setSelectedDate(null)} className="text-xs text-milk-tea-500 hover:text-milk-tea-800">Clear Date</button>
          )}
      </div>

      <div className="space-y-4 pb-24">
        {filteredRecords.map(record => (
          <div 
            key={record.id} 
            onClick={() => onViewRecord?.(record)}
            className="bg-white p-4 rounded-2xl flex gap-4 shadow-sm shadow-milk-tea-100 border border-transparent hover:border-milk-tea-200 transition-colors group relative cursor-pointer"
          >
            <div className="w-16 h-16 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative">
                {record.imageUrl ? (
                    <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                ) : (
                    <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-2xl font-bold opacity-50">
                        {record.brand[0]}
                    </div>
                )}
            </div>
            <div className="flex-1 min-w-0">
              <div className="flex justify-between items-start">
                <div>
                    <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                    <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                </div>
                <div className="flex flex-col items-end">
                    <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
                    <span className="text-xs font-bold text-milk-tea-800 mt-1">¥{record.price}</span>
                </div>
              </div>

              <div className="mt-2 flex flex-wrap gap-2">
                <span className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                    {record.sugar || 'Regular'}
                </span>
                {record.tags && record.tags.map((tag, i) => (
                    <span key={i} className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                        {tag}
                    </span>
                ))}
              </div>
            </div>

            {/* Action Buttons (Always visible on mobile/desktop for accessibility) */}
            <div className="absolute top-2 right-2 flex gap-2 bg-white/60 backdrop-blur rounded-lg p-1 z-10 border border-white/20 shadow-sm">
                <button 
                    onClick={(e) => { 
                        e.stopPropagation(); 
                        e.preventDefault();
                        onEdit?.(record); 
                    }} 
                    className="p-1.5 text-milk-tea-500 hover:text-milk-tea-800 hover:bg-milk-tea-100 rounded-md transition-colors"
                    aria-label="Edit"
                >
                    <Edit2 size={14} />
                </button>
                <button 
                    onClick={(e) => { 
                        e.stopPropagation(); 
                        e.preventDefault();
                        onDelete?.(record.id); 
                    }} 
                    className="p-1.5 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-md transition-colors"
                    aria-label="Delete"
                >
                    <Trash2 size={14} />
                </button>
            </div>
          </div>
        ))}
        {filteredRecords.length === 0 && (
            <div className="text-center py-12 text-milk-tea-300">
                <p>{searchQuery ? 'No matches found.' : 'No bubble tea recorded.'}</p>
            </div>
        )}
      </div>
    </div>
  );
};

import React, { useMemo, useState } from 'react';
import { TeaRecord } from '../types';
import { Search, X, ChevronDown, CupSoda, ArrowUpDown } from 'lucide-react';

interface LibraryViewProps {
  records: TeaRecord[];
}

interface DrinkGroup {
    name: string;
    records: TeaRecord[];
    count: number;
    avgRating: number;
    latestTimestamp: number;
}

interface BrandGroup {
    brand: string;
    drinks: DrinkGroup[];
    totalCount: number;
    avgRating: number;
}

type SortOption = 'frequency' | 'rating' | 'date';

export const LibraryView: React.FC<LibraryViewProps> = ({ records }) => {
  const [searchQuery, setSearchQuery] = useState('');
  const [expandedBrand, setExpandedBrand] = useState<string | null>(null);
  const [sortBy, setSortBy] = useState<SortOption>('frequency');

  // Grouping Logic
  const groupedData: BrandGroup[] = useMemo(() => {
    if (searchQuery) return []; 

    const brands: Record<string, Record<string, TeaRecord[]>> = {};

    // 1. Group by Brand -> Drink Name
    records.forEach(r => {
        if (!brands[r.brand]) brands[r.brand] = {};
        if (!brands[r.brand][r.name]) brands[r.brand][r.name] = [];
        brands[r.brand][r.name].push(r);
    });

    // 2. Transform to Array structure
    const brandGroups: BrandGroup[] = Object.entries(brands).map(([brandName, drinkMap]) => {
        const drinks: DrinkGroup[] = Object.entries(drinkMap).map(([drinkName, drinkRecords]) => {
            const sumRating = drinkRecords.reduce((sum, r) => sum + r.rating, 0);
            const latest = Math.max(...drinkRecords.map(r => r.timestamp));

            return {
                name: drinkName,
                records: drinkRecords,
                count: drinkRecords.length,
                avgRating: sumRating / drinkRecords.length,
                latestTimestamp: latest
            };
        });

        // Dynamic Sorting based on sortBy state
        drinks.sort((a, b) => {
            if (sortBy === 'frequency') {
                if (b.count !== a.count) return b.count - a.count;
                if (b.avgRating !== a.avgRating) return b.avgRating - a.avgRating;
                return b.latestTimestamp - a.latestTimestamp;
            } else if (sortBy === 'rating') {
                if (b.avgRating !== a.avgRating) return b.avgRating - a.avgRating;
                if (b.count !== a.count) return b.count - a.count;
                return b.latestTimestamp - a.latestTimestamp;
            } else { // date
                return b.latestTimestamp - a.latestTimestamp;
            }
        });

        const totalCount = drinks.reduce((sum, d) => sum + d.count, 0);
        // Calculate weighted average for brand
        const allBrandRecords = drinks.flatMap(d => d.records);
        const brandAvgRating = allBrandRecords.reduce((sum, r) => sum + r.rating, 0) / allBrandRecords.length;

        return {
            brand: brandName,
            drinks,
            totalCount,
            avgRating: brandAvgRating
        };
    });

    // Sort Brands alphabetically
    brandGroups.sort((a, b) => a.brand.localeCompare(b.brand));

    return brandGroups;
  }, [records, searchQuery, sortBy]);

  // Search Logic
  const filteredRecords = useMemo(() => {
    if (!searchQuery.trim()) return [];
    const lowerQuery = searchQuery.toLowerCase();
    return records.filter(
      r => 
        r.brand.toLowerCase().includes(lowerQuery) || 
        r.name.toLowerCase().includes(lowerQuery)
    );
  }, [records, searchQuery]);

  const renderStars = (rating: number) => {
    return "★".repeat(Math.round(rating)) + "☆".repeat(5 - Math.round(rating));
  };

  const toggleBrand = (brand: string) => {
      setExpandedBrand(expandedBrand === brand ? null : brand);
  };

  const SortButton = ({ type, label }: { type: SortOption, label: string }) => (
    <button 
        onClick={() => setSortBy(type)}
        className={`px-4 py-1.5 rounded-full text-xs font-bold transition-all border ${
            sortBy === type 
                ? 'bg-milk-tea-800 text-white border-milk-tea-800' 
                : 'bg-white text-milk-tea-500 border-milk-tea-100 hover:bg-milk-tea-50'
        }`}
    >
        {label}
    </button>
  );

  return (
    <div className="p-6 pb-24 max-w-md mx-auto animate-fade-in">
      <div className="sticky top-0 bg-cream/95 backdrop-blur-sm z-10 pb-4 -mx-6 px-6 pt-2">
        <h1 className="text-3xl font-bold text-milk-tea-900 mb-4">Tasting Log</h1>

        {/* Search Bar */}
        <div className="relative mb-3">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-milk-tea-300" size={20} />
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search brand or drink..."
            className="w-full pl-12 pr-10 py-3 bg-white rounded-2xl border border-milk-tea-100 text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-200 placeholder:text-milk-tea-200 transition-all shadow-sm"
          />
          {searchQuery && (
            <button 
              onClick={() => setSearchQuery('')}
              className="absolute right-3 top-1/2 -translate-y-1/2 text-milk-tea-300 hover:text-milk-tea-500 p-1"
            >
              <X size={16} />
            </button>
          )}
        </div>

        {/* Sort Controls (Only visible when not searching) */}
        {!searchQuery && (
            <div className="flex gap-2 overflow-x-auto no-scrollbar pb-1">
                <div className="flex items-center text-milk-tea-400 mr-1">
                    <ArrowUpDown size={14} />
                </div>
                <SortButton type="frequency" label="Most Frequent" />
                <SortButton type="rating" label="Highest Rated" />
                <SortButton type="date" label="Newest" />
            </div>
        )}
      </div>

      <div className="space-y-4 mt-2">
        {/* Accordion Brand List */}
        {!searchQuery && groupedData.map((group) => {
            const isExpanded = expandedBrand === group.brand;
            return (
                <div key={group.brand} className="bg-white rounded-3xl overflow-hidden shadow-sm border border-milk-tea-100 transition-all duration-300">
                    <button 
                        onClick={() => toggleBrand(group.brand)}
                        className={`w-full flex justify-between items-center p-5 text-left transition-colors ${isExpanded ? 'bg-milk-tea-50/50' : 'hover:bg-milk-tea-50/30'}`}
                    >
                        <div className="flex items-center gap-4">
                             <div className="w-12 h-12 rounded-full bg-milk-tea-100 flex items-center justify-center text-milk-tea-800 font-bold text-xl">
                                {group.brand[0]}
                             </div>
                             <div>
                                <h2 className="font-bold text-milk-tea-900 text-lg">{group.brand}</h2>
                                <p className="text-xs text-milk-tea-500 font-medium">
                                    {group.totalCount} drinks • {group.avgRating.toFixed(1)} avg
                                </p>
                             </div>
                        </div>
                        <div className={`text-milk-tea-400 transition-transform duration-300 ${isExpanded ? 'rotate-180' : ''}`}>
                            <ChevronDown size={20} />
                        </div>
                    </button>

                    {/* Expanded Content */}
                    {isExpanded && (
                        <div className="bg-white border-t border-milk-tea-50 animate-fade-in">
                            {group.drinks.map((drink, idx) => (
                                <div key={`${drink.name}-${idx}`} className="p-4 flex items-center justify-between border-b border-milk-tea-50 last:border-none hover:bg-cream/50 transition-colors">
                                    <div className="flex items-start gap-3">
                                        <div className="mt-1 text-milk-tea-300">
                                            <CupSoda size={16} />
                                        </div>
                                        <div>
                                            <p className="font-bold text-milk-tea-800 text-sm">{drink.name}</p>
                                            <div className="flex gap-2 mt-1">
                                                <span className="px-1.5 py-0.5 bg-milk-tea-100 text-milk-tea-800 text-[10px] rounded font-medium">
                                                    {drink.count} cups
                                                </span>
                                                <span className="text-[10px] text-milk-tea-400 flex items-center">
                                                    Last: {new Date(drink.latestTimestamp).toLocaleDateString()}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="text-right">
                                        <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(drink.avgRating)}</span>
                                        <p className="text-[10px] text-milk-tea-400 font-medium mt-0.5">
                                            {drink.avgRating.toFixed(1)}
                                        </p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            )
        })}

        {/* Search Results (Flat List) */}
        {searchQuery && filteredRecords.map(record => (
             <div key={record.id} className="bg-white p-4 rounded-2xl flex gap-4 shadow-sm shadow-milk-tea-100 border border-transparent">
                <div className="w-14 h-14 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative">
                    {record.imageUrl ? (
                        <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                    ) : (
                        <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-xl font-bold opacity-50">
                            {record.brand[0]}
                        </div>
                    )}
                </div>
                <div className="flex-1 min-w-0">
                    <div className="flex justify-between items-start">
                        <div>
                            <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                            <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                        </div>
                        <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
                    </div>
                    <div className="mt-1 flex flex-wrap gap-2">
                         <p className="text-[11px] text-milk-tea-400">
                             {record.sugar} • {record.ice}
                         </p>
                    </div>
                </div>
             </div>
        ))}

        {records.length > 0 && searchQuery && filteredRecords.length === 0 && (
             <div className="text-center text-milk-tea-300 mt-10">
                <p>No matches found for "{searchQuery}"</p>
            </div>
        )}

        {records.length === 0 && (
            <div className="text-center text-milk-tea-300 mt-20 flex flex-col items-center">
                <p>Your library is empty.</p>
                <p className="text-sm">Start drinking!</p>
            </div>
        )}
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { TeaRecord } from '../types';
import { ChevronLeft, ChevronRight, Edit2, Trash2, Search, X, ChevronDown } from 'lucide-react';

interface CalendarViewProps {
  records: TeaRecord[];
  onDelete?: (id: string) => void;
  onEdit?: (record: TeaRecord) => void;
  onViewRecord?: (record: TeaRecord) => void;
}

export const CalendarView: React.FC<CalendarViewProps> = ({ records, onDelete, onEdit, onViewRecord }) => {
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isDatePickerOpen, setIsDatePickerOpen] = useState(false);

  // Month navigation
  const prevMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  const nextMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));

  // Dropdown data
  const currentYear = new Date().getFullYear();
  const years = Array.from({ length: 11 }, (_, i) => currentYear - 5 + i); // +/- 5 years
  const months = Array.from({ length: 12 }, (_, i) => i);

  const getDaysInMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
  };

  const daysInMonth = getDaysInMonth(currentMonth);
  const firstDay = getFirstDayOfMonth(currentMonth);

  // Filter records
  const filteredRecords = records.filter(record => {
    const d = new Date(record.timestamp);
    const matchesMonth = d.getMonth() === currentMonth.getMonth() && d.getFullYear() === currentMonth.getFullYear();
    const matchesDate = selectedDate ? d.getDate() === selectedDate.getDate() : true;
    const matchesSearch = searchQuery 
        ? record.name.toLowerCase().includes(searchQuery.toLowerCase()) || record.brand.toLowerCase().includes(searchQuery.toLowerCase())
        : true;

    if (searchQuery) return matchesSearch;
    return matchesMonth && matchesDate;
  }).sort((a,b) => b.timestamp - a.timestamp);

  // Group records for calendar dots (visual only)
  const recordsByDay = records.reduce((acc, record) => {
    const d = new Date(record.timestamp);
    if (d.getMonth() === currentMonth.getMonth() && d.getFullYear() === currentMonth.getFullYear()) {
      const day = d.getDate();
      if (!acc[day]) acc[day] = [];
      acc[day].push(record);
    }
    return acc;
  }, {} as Record<number, TeaRecord[]>);

  const renderStars = (rating: number) => {
    return "★".repeat(rating) + "☆".repeat(5 - rating);
  };

  return (
    <div className="p-6 max-w-md mx-auto animate-fade-in">
        <header className="mb-6 space-y-4">
            <h1 className="text-3xl font-bold text-milk-tea-900">Tea Journal</h1>

            {/* Search Bar */}
            <div className="relative z-10">
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-milk-tea-300" size={18} />
                <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    placeholder="Search your history..."
                    className="w-full pl-10 pr-4 py-2.5 bg-white rounded-xl border border-milk-tea-100 text-sm text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-200 placeholder:text-milk-tea-200 transition-all shadow-sm"
                />
                {searchQuery && (
                    <button 
                    onClick={() => setSearchQuery('')}
                    className="absolute right-3 top-1/2 -translate-y-1/2 text-milk-tea-300 hover:text-milk-tea-500 p-1"
                    >
                    <X size={14} />
                    </button>
                )}
            </div>

            {/* Month Nav - Hidden if searching */}
            {!searchQuery && (
                <div className="relative flex items-center justify-center bg-white/50 p-2 rounded-2xl backdrop-blur-sm z-20 min-h-[56px]">
                    <button onClick={prevMonth} className="absolute left-2 p-2 text-milk-tea-600 hover:bg-white rounded-xl transition-colors">
                        <ChevronLeft size={20} />
                    </button>

                    {/* Centered Date Picker Button */}
                    <button 
                        onClick={() => setIsDatePickerOpen(!isDatePickerOpen)}
                        className="flex items-center gap-2 px-4 py-1.5 bg-milk-tea-50/50 rounded-full cursor-pointer hover:bg-milk-tea-100 transition-colors"
                    >
                        <span className="text-milk-tea-800 font-bold text-sm uppercase tracking-widest">
                            {currentMonth.toLocaleString('default', { month: 'long', year: 'numeric' })}
                        </span>
                        <ChevronDown size={16} className={`text-milk-tea-400 transition-transform duration-300 ${isDatePickerOpen ? 'rotate-180' : ''}`} />
                    </button>

                    {/* Custom Date Picker Popup - Centered relative to the header container */}
                    {isDatePickerOpen && (
                        <>
                            {/* Invisible backdrop to close on outside click */}
                            <div className="fixed inset-0 z-40" onClick={() => setIsDatePickerOpen(false)} />

                            <div className="absolute top-full mt-2 left-1/2 -translate-x-1/2 bg-white/95 backdrop-blur-xl p-3 rounded-2xl shadow-[0_10px_40px_rgba(0,0,0,0.12)] border border-white/50 z-50 flex gap-2 min-w-[240px] animate-fade-in origin-top" onClick={(e) => e.stopPropagation()}>
                                <div className="relative flex-1">
                                    <select 
                                        value={currentMonth.getFullYear()}
                                        onChange={(e) => {
                                            const newYear = parseInt(e.target.value);
                                            setCurrentMonth(new Date(newYear, currentMonth.getMonth(), 1));
                                        }}
                                        className="w-full p-2 bg-milk-tea-50 rounded-xl text-sm font-bold text-milk-tea-900 focus:outline-none appearance-none text-center hover:bg-milk-tea-100 transition-colors cursor-pointer"
                                    >
                                        {years.map(y => <option key={y} value={y}>{y}</option>)}
                                    </select>
                                    <div className="absolute inset-y-0 right-1 flex items-center pointer-events-none">
                                        <ChevronDown size={12} className="text-milk-tea-400" />
                                    </div>
                                </div>

                                <div className="relative flex-1">
                                    <select 
                                        value={currentMonth.getMonth()}
                                        onChange={(e) => {
                                            const newMonth = parseInt(e.target.value);
                                            setCurrentMonth(new Date(currentMonth.getFullYear(), newMonth, 1));
                                        }}
                                        className="w-full p-2 bg-milk-tea-50 rounded-xl text-sm font-bold text-milk-tea-900 focus:outline-none appearance-none text-center hover:bg-milk-tea-100 transition-colors cursor-pointer"
                                    >
                                        {months.map(m => (
                                            <option key={m} value={m}>
                                                {new Date(2000, m, 1).toLocaleString('default', { month: 'short' })}
                                            </option>
                                        ))}
                                    </select>
                                    <div className="absolute inset-y-0 right-1 flex items-center pointer-events-none">
                                        <ChevronDown size={12} className="text-milk-tea-400" />
                                    </div>
                                </div>
                            </div>
                        </>
                    )}

                    <button onClick={nextMonth} className="absolute right-2 p-2 text-milk-tea-600 hover:bg-white rounded-xl transition-colors">
                        <ChevronRight size={20} />
                    </button>
                </div>
            )}
        </header>

      {/* Calendar Grid - Hidden if searching */}
      {!searchQuery && (
          <div className="bg-white rounded-3xl p-6 shadow-sm shadow-milk-tea-100 mb-8 transition-all relative z-0">
            <div className="grid grid-cols-7 gap-2 mb-4">
              {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map(d => (
                <div key={d} className="text-center text-xs text-milk-tea-400 font-bold">{d}</div>
              ))}
            </div>
            <div className="grid grid-cols-7 gap-y-4 gap-x-2">
              {Array.from({ length: firstDay }).map((_, i) => (
                <div key={`empty-${i}`} />
              ))}
              {Array.from({ length: daysInMonth }).map((_, i) => {
                const day = i + 1;
                const dailyRecords = recordsByDay[day] || [];
                const count = dailyRecords.length;
                const hasTea = count > 0;
                const isSelected = selectedDate?.getDate() === day && selectedDate?.getMonth() === currentMonth.getMonth();

                // Determine background color based on intensity
                let bgClass = '';
                let textClass = '';

                if (isSelected) {
                   bgClass = 'bg-milk-tea-800 shadow-md scale-110';
                   textClass = 'text-white';
                } else if (count === 0) {
                   bgClass = 'text-milk-tea-300 hover:bg-cream';
                   textClass = 'text-milk-tea-300';
                } else if (count === 1) {
                   bgClass = 'bg-milk-tea-100 hover:bg-milk-tea-200';
                   textClass = 'text-milk-tea-800';
                } else if (count === 2) {
                   bgClass = 'bg-milk-tea-200 hover:bg-[#EBCFBF]';
                   textClass = 'text-milk-tea-900';
                } else {
                   // 3 or more
                   bgClass = 'bg-milk-tea-500 hover:bg-milk-tea-800';
                   textClass = 'text-white';
                }

                // Dot Logic: Max 3 dots
                const dotsCount = Math.min(count, 3);

                return (
                  <button 
                    key={day} 
                    onClick={() => {
                        // Toggle selection
                        if (isSelected) setSelectedDate(null);
                        else {
                            const newDate = new Date(currentMonth);
                            newDate.setDate(day);
                            setSelectedDate(newDate);
                        }
                    }}
                    className="flex flex-col items-center gap-1 group relative"
                  >
                    <span className={`text-sm font-medium w-8 h-8 flex items-center justify-center rounded-full transition-all duration-300 ${bgClass} ${textClass}`}>
                      {day}
                    </span>
                    {/* Dots always visible if hasTea */}
                    {hasTea && (
                        <div className="flex gap-0.5 absolute -bottom-1.5">
                            {Array.from({ length: dotsCount }).map((_, idx) => {
                                // If background is dark (selected or high count), dots are white. Else brown.
                                const isDarkBg = isSelected || count >= 3;
                                const dotColor = isDarkBg ? 'bg-white/90' : 'bg-milk-tea-800/70';
                                return (
                                    <div key={idx} className={`w-1 h-1 rounded-full ${dotColor}`} />
                                );
                            })}
                        </div>
                    )}
                  </button>
                );
              })}
            </div>
          </div>
      )}

      {/* Filtered Records List */}
      <div className="flex justify-between items-center mb-4 px-1">
          <h3 className="text-lg font-bold text-milk-tea-800">
            {searchQuery ? 'Search Results' : selectedDate ? 'Daily Sips' : 'Recent Sips'}
          </h3>
          {selectedDate && !searchQuery && (
              <button onClick={() => setSelectedDate(null)} className="text-xs text-milk-tea-500 hover:text-milk-tea-800">Clear Date</button>
          )}
      </div>

      <div className="space-y-4 pb-24">
        {filteredRecords.map(record => (
          <div 
            key={record.id} 
            onClick={() => onViewRecord?.(record)}
            className="bg-white p-4 rounded-2xl flex gap-4 shadow-sm shadow-milk-tea-100 border border-transparent hover:border-milk-tea-200 transition-colors group relative cursor-pointer"
          >
            <div className="w-16 h-16 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative">
                {record.imageUrl ? (
                    <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                ) : (
                    <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-2xl font-bold opacity-50">
                        {record.brand[0]}
                    </div>
                )}
            </div>
            <div className="flex-1 min-w-0">
              <div className="flex justify-between items-start">
                <div>
                    <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                    <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                </div>
                <div className="flex flex-col items-end">
                    <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
                    <span className="text-xs font-bold text-milk-tea-800 mt-1">¥{record.price}</span>
                </div>
              </div>

              <div className="mt-2 flex flex-wrap gap-2">
                <span className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                    {record.sugar || 'Regular'}
                </span>
                {record.tags && record.tags.map((tag, i) => (
                    <span key={i} className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                        {tag}
                    </span>
                ))}
              </div>
            </div>

            {/* Action Buttons (High Z-Index to fix clicking issues) */}
            <div className="absolute top-2 right-2 flex gap-2 bg-white/60 backdrop-blur rounded-lg p-1 z-50 border border-white/20 shadow-sm" onClick={(e) => e.stopPropagation()}>
                <button 
                    onClick={(e) => { 
                        e.stopPropagation(); 
                        if (onEdit) onEdit(record); 
                    }} 
                    className="p-1.5 text-milk-tea-500 hover:text-milk-tea-800 hover:bg-milk-tea-100 rounded-md transition-colors"
                    aria-label="Edit"
                >
                    <Edit2 size={14} className="pointer-events-none" />
                </button>
                <button 
                    onClick={(e) => { 
                        e.stopPropagation(); 
                        if (onDelete) onDelete(record.id); 
                    }} 
                    className="p-1.5 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-md transition-colors"
                    aria-label="Delete"
                >
                    <Trash2 size={14} className="pointer-events-none" />
                </button>
            </div>
          </div>
        ))}
        {filteredRecords.length === 0 && (
            <div className="text-center py-12 text-milk-tea-300">
                <p>{searchQuery ? 'No matches found.' : 'No bubble tea recorded.'}</p>
            </div>
        )}
      </div>
    </div>
  );
};

import React, { useEffect, useState } from 'react';
import { Screen, TeaRecord } from './types';
import { loadRecords, saveRecords, seedInitialData } from './services/storageService';
import { Navigation } from './components/Navigation';
import { CalendarView } from './components/CalendarView';
import { RecordForm } from './components/RecordForm';
import { StatsView } from './components/StatsView';
import { LibraryView } from './components/LibraryView';
import { RecordDetailModal } from './components/RecordDetailModal';

export default function App() {
  const [screen, setScreen] = useState<Screen>('calendar');
  const [records, setRecords] = useState<TeaRecord[]>([]);
  const [editingRecord, setEditingRecord] = useState<TeaRecord | undefined>(undefined);
  const [viewingRecord, setViewingRecord] = useState<TeaRecord | null>(null);

  // Initialize Data
  useEffect(() => {
    const data = seedInitialData();
    setRecords(data);
  }, []);

  const handleSaveRecord = (newRecordData: Omit<TeaRecord, 'id' | 'timestamp'>) => {
    let updatedRecords;

    if (editingRecord) {
      // Update existing
      updatedRecords = records.map(r => 
        r.id === editingRecord.id 
          ? { ...newRecordData, id: editingRecord.id, timestamp: editingRecord.timestamp } 
          : r
      );
      setEditingRecord(undefined);
    } else {
      // Create new
      const record: TeaRecord = {
        ...newRecordData,
        id: crypto.randomUUID(),
        timestamp: Date.now(),
      };
      updatedRecords = [...records, record];
    }

    setRecords(updatedRecords);
    saveRecords(updatedRecords);
    setScreen('calendar');
  };

  const handleDeleteRecord = (id: string) => {
    // Removed window.confirm to resolve "no reaction" issue on some devices/environments
    const updated = records.filter(r => r.id !== id);
    setRecords(updated);
    saveRecords(updated);
    // Close detail view if deleting the currently viewed record
    if (viewingRecord?.id === id) {
        setViewingRecord(null);
    }
  };

  const handleEditRecord = (record: TeaRecord) => {
    setEditingRecord(record);
    setScreen('record');
    setViewingRecord(null); // Close detail view if open
  };

  const handleCancelRecord = () => {
    setEditingRecord(undefined);
    setScreen('calendar');
  };

  const renderScreen = () => {
    switch (screen) {
      case 'calendar':
        return <CalendarView records={records} onDelete={handleDeleteRecord} onEdit={handleEditRecord} onViewRecord={setViewingRecord} />;
      case 'record':
        // Navigation handles showing form overlay or navigating
        return <CalendarView records={records} onDelete={handleDeleteRecord} onEdit={handleEditRecord} />; 
      case 'stats':
        return <StatsView records={records} />;
      case 'library':
        return <LibraryView records={records} />;
      default:
        return <CalendarView records={records} onDelete={handleDeleteRecord} onEdit={handleEditRecord} onViewRecord={setViewingRecord} />;
    }
  };

  return (
    <div className="min-h-screen bg-cream relative max-w-lg mx-auto shadow-2xl overflow-hidden font-sans">

      {/* Main Content Area */}
      <main className="h-full overflow-y-auto no-scrollbar pb-32">
         {renderScreen()}
      </main>

      {/* Record Overlay Modal (Edit/New) */}
      {screen === 'record' && (
        <div className="fixed inset-0 z-50 bg-cream animate-slide-up overflow-y-auto">
            <RecordForm 
                initialData={editingRecord}
                onSave={handleSaveRecord} 
                onCancel={handleCancelRecord} 
            />
        </div>
      )}

      {/* View Detail Modal (Read Only) */}
      {viewingRecord && (
          <RecordDetailModal 
            record={viewingRecord} 
            onClose={() => setViewingRecord(null)} 
          />
      )}

      {/* Navigation - Hide when in record mode */}
      {screen !== 'record' && (
        <Navigation currentScreen={screen} onNavigate={setScreen} />
      )}

      <style>{`
        @keyframes fade-in {
            from { opacity: 0; transform: translateY(10px); }
            to { opacity: 1; transform: translateY(0); }
        }
        @keyframes slide-up {
            from { transform: translateY(100%); }
            to { transform: translateY(0); }
        }
        .animate-fade-in {
            animation: fade-in 0.4s ease-out forwards;
        }
        .animate-slide-up {
            animation: slide-up 0.3s cubic-bezier(0.16, 1, 0.3, 1) forwards;
        }
      `}</style>
    </div>
  );
}

import React, { useMemo, useState } from 'react';
import { TeaRecord } from '../types';
import { Search, X, ChevronDown, CupSoda } from 'lucide-react';

interface LibraryViewProps {
  records: TeaRecord[];
}

interface DrinkGroup {
    name: string;
    records: TeaRecord[];
    count: number;
    avgRating: number;
    latestTimestamp: number;
}

interface BrandGroup {
    brand: string;
    drinks: DrinkGroup[];
    totalCount: number;
    avgRating: number;
    latestTimestamp: number;
}

type SortOption = 'frequency' | 'rating' | 'date';

export const LibraryView: React.FC<LibraryViewProps> = ({ records }) => {
  const [searchQuery, setSearchQuery] = useState('');
  // Changed to Set to support multiple expanded brands
  const [expandedBrands, setExpandedBrands] = useState<Set<string>>(new Set());
  const [sortBy, setSortBy] = useState<SortOption>('frequency');

  // Grouping Logic
  const groupedData: BrandGroup[] = useMemo(() => {
    if (searchQuery) return []; 

    const brands: Record<string, Record<string, TeaRecord[]>> = {};

    // 1. Group by Brand -> Drink Name
    records.forEach(r => {
        if (!brands[r.brand]) brands[r.brand] = {};
        if (!brands[r.brand][r.name]) brands[r.brand][r.name] = [];
        brands[r.brand][r.name].push(r);
    });

    // 2. Transform to Array structure
    const brandGroups: BrandGroup[] = Object.entries(brands).map(([brandName, drinkMap]) => {
        const drinks: DrinkGroup[] = Object.entries(drinkMap).map(([drinkName, drinkRecords]) => {
            const sumRating = drinkRecords.reduce((sum, r) => sum + r.rating, 0);
            const latest = Math.max(...drinkRecords.map(r => r.timestamp));

            return {
                name: drinkName,
                records: drinkRecords,
                count: drinkRecords.length,
                avgRating: sumRating / drinkRecords.length,
                latestTimestamp: latest
            };
        });

        // Sort Drinks inside the brand based on current sort option
        drinks.sort((a, b) => {
            if (sortBy === 'frequency') {
                if (b.count !== a.count) return b.count - a.count;
                return b.latestTimestamp - a.latestTimestamp;
            } else if (sortBy === 'rating') {
                if (b.avgRating !== a.avgRating) return b.avgRating - a.avgRating;
                return b.latestTimestamp - a.latestTimestamp;
            } else { // date
                return b.latestTimestamp - a.latestTimestamp;
            }
        });

        const totalCount = drinks.reduce((sum, d) => sum + d.count, 0);

        // Calculate weighted average for brand
        const allBrandRecords = drinks.flatMap(d => d.records);
        const brandAvgRating = allBrandRecords.reduce((sum, r) => sum + r.rating, 0) / allBrandRecords.length;

        // Find latest timestamp for the brand
        const brandLatestTimestamp = Math.max(...drinks.map(d => d.latestTimestamp));

        return {
            brand: brandName,
            drinks,
            totalCount,
            avgRating: brandAvgRating,
            latestTimestamp: brandLatestTimestamp
        };
    });

    // 3. Sort Brands based on the selected criteria
    brandGroups.sort((a, b) => {
        if (sortBy === 'frequency') {
            // Sort by total count desc
            if (b.totalCount !== a.totalCount) return b.totalCount - a.totalCount;
            return a.brand.localeCompare(b.brand);
        } else if (sortBy === 'rating') {
            // Sort by average rating desc
            if (b.avgRating !== a.avgRating) return b.avgRating - a.avgRating;
            return a.brand.localeCompare(b.brand);
        } else { // date
            // Sort by latest timestamp desc
            return b.latestTimestamp - a.latestTimestamp;
        }
    });

    return brandGroups;
  }, [records, searchQuery, sortBy]);

  // Search Logic
  const filteredRecords = useMemo(() => {
    if (!searchQuery.trim()) return [];
    const lowerQuery = searchQuery.toLowerCase();
    return records.filter(
      r => 
        r.brand.toLowerCase().includes(lowerQuery) || 
        r.name.toLowerCase().includes(lowerQuery)
    );
  }, [records, searchQuery]);

  const renderStars = (rating: number) => {
    return "★".repeat(Math.round(rating)) + "☆".repeat(5 - Math.round(rating));
  };

  const toggleBrand = (brand: string) => {
      const newSet = new Set(expandedBrands);
      if (newSet.has(brand)) {
          newSet.delete(brand);
      } else {
          newSet.add(brand);
      }
      setExpandedBrands(newSet);
  };

  const SortButton = ({ type, label }: { type: SortOption, label: string }) => (
    <button 
        onClick={() => setSortBy(type)}
        className={`px-4 py-1.5 rounded-full text-xs font-bold transition-all border ${
            sortBy === type 
                ? 'bg-milk-tea-800 text-white border-milk-tea-800' 
                : 'bg-white text-milk-tea-500 border-milk-tea-100 hover:bg-milk-tea-50'
        }`}
    >
        {label}
    </button>
  );

  return (
    <div className="p-6 pb-24 max-w-md mx-auto animate-fade-in">
      <div className="sticky top-0 bg-cream/95 backdrop-blur-sm z-10 pb-4 -mx-6 px-6 pt-2">
        <h1 className="text-3xl font-bold text-milk-tea-900 mb-4">Tasting Log</h1>

        {/* Search Bar */}
        <div className="relative mb-3">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-milk-tea-300" size={20} />
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search brand or drink..."
            className="w-full pl-12 pr-10 py-3 bg-white rounded-2xl border border-milk-tea-100 text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-200 placeholder:text-milk-tea-200 transition-all shadow-sm"
          />
          {searchQuery && (
            <button 
              onClick={() => setSearchQuery('')}
              className="absolute right-3 top-1/2 -translate-y-1/2 text-milk-tea-300 hover:text-milk-tea-500 p-1"
            >
              <X size={16} />
            </button>
          )}
        </div>

        {/* Sort Controls (Only visible when not searching) */}
        {!searchQuery && (
            <div className="flex gap-2 overflow-x-auto no-scrollbar pb-1">
                {/* Icon removed here */}
                <SortButton type="frequency" label="Most Frequent" />
                <SortButton type="rating" label="Highest Rated" />
                <SortButton type="date" label="Newest" />
            </div>
        )}
      </div>

      <div className="space-y-4 mt-2">
        {/* Accordion Brand List */}
        {!searchQuery && groupedData.map((group) => {
            const isExpanded = expandedBrands.has(group.brand);
            return (
                <div key={group.brand} className="bg-white rounded-3xl overflow-hidden shadow-sm border border-milk-tea-100 transition-all duration-300">
                    <button 
                        onClick={() => toggleBrand(group.brand)}
                        className={`w-full flex justify-between items-center p-5 text-left transition-colors ${isExpanded ? 'bg-milk-tea-50/50' : 'hover:bg-milk-tea-50/30'}`}
                    >
                        <div className="flex items-center gap-4">
                             <div className="w-12 h-12 rounded-full bg-milk-tea-100 flex items-center justify-center text-milk-tea-800 font-bold text-xl">
                                {group.brand[0]}
                             </div>
                             <div>
                                <h2 className="font-bold text-milk-tea-900 text-lg">{group.brand}</h2>
                                <p className="text-xs text-milk-tea-500 font-medium">
                                    {group.totalCount} drinks • {group.avgRating.toFixed(1)} avg
                                </p>
                             </div>
                        </div>
                        <div className={`text-milk-tea-400 transition-transform duration-300 ${isExpanded ? 'rotate-180' : ''}`}>
                            <ChevronDown size={20} />
                        </div>
                    </button>

                    {/* Expanded Content */}
                    {isExpanded && (
                        <div className="bg-white border-t border-milk-tea-50 animate-fade-in">
                            {group.drinks.map((drink, idx) => (
                                <div key={`${drink.name}-${idx}`} className="p-4 flex items-center justify-between border-b border-milk-tea-50 last:border-none hover:bg-cream/50 transition-colors">
                                    <div className="flex items-start gap-3">
                                        <div className="mt-1 text-milk-tea-300">
                                            <CupSoda size={16} />
                                        </div>
                                        <div>
                                            <p className="font-bold text-milk-tea-800 text-sm">{drink.name}</p>
                                            <div className="flex gap-2 mt-1">
                                                <span className="px-1.5 py-0.5 bg-milk-tea-100 text-milk-tea-800 text-[10px] rounded font-medium">
                                                    {drink.count} cups
                                                </span>
                                                <span className="text-[10px] text-milk-tea-400 flex items-center">
                                                    Last: {new Date(drink.latestTimestamp).toLocaleDateString()}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="text-right">
                                        <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(drink.avgRating)}</span>
                                        <p className="text-[10px] text-milk-tea-400 font-medium mt-0.5">
                                            {drink.avgRating.toFixed(1)}
                                        </p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            )
        })}

        {/* Search Results (Flat List) */}
        {searchQuery && filteredRecords.map(record => (
             <div key={record.id} className="bg-white p-4 rounded-2xl flex gap-4 shadow-sm shadow-milk-tea-100 border border-transparent">
                <div className="w-14 h-14 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative">
                    {record.imageUrl ? (
                        <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                    ) : (
                        <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-xl font-bold opacity-50">
                            {record.brand[0]}
                        </div>
                    )}
                </div>
                <div className="flex-1 min-w-0">
                    <div className="flex justify-between items-start">
                        <div>
                            <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                            <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                        </div>
                        <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
                    </div>
                    <div className="mt-1 flex flex-wrap gap-2">
                         <p className="text-[11px] text-milk-tea-400">
                             {record.sugar} • {record.ice}
                         </p>
                    </div>
                </div>
             </div>
        ))}

        {records.length > 0 && searchQuery && filteredRecords.length === 0 && (
             <div className="text-center text-milk-tea-300 mt-10">
                <p>No matches found for "{searchQuery}"</p>
            </div>
        )}

        {records.length === 0 && (
            <div className="text-center text-milk-tea-300 mt-20 flex flex-col items-center">
                <p>Your library is empty.</p>
                <p className="text-sm">Start drinking!</p>
            </div>
        )}
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { TeaRecord } from '../types';
import { ChevronLeft, ChevronRight, Edit2, Trash2, Search, X, ChevronDown } from 'lucide-react';

interface CalendarViewProps {
  records: TeaRecord[];
  onDelete?: (id: string) => void;
  onEdit?: (record: TeaRecord) => void;
  onViewRecord?: (record: TeaRecord) => void;
}

export const CalendarView: React.FC<CalendarViewProps> = ({ records, onDelete, onEdit, onViewRecord }) => {
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isDatePickerOpen, setIsDatePickerOpen] = useState(false);

  // Month navigation
  const prevMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  const nextMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));

  // Dropdown data
  const currentYear = new Date().getFullYear();
  const years = Array.from({ length: 11 }, (_, i) => currentYear - 5 + i); // +/- 5 years
  const months = Array.from({ length: 12 }, (_, i) => i);

  const getDaysInMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
  };

  const daysInMonth = getDaysInMonth(currentMonth);
  const firstDay = getFirstDayOfMonth(currentMonth);

  // Filter records
  const filteredRecords = records.filter(record => {
    const d = new Date(record.timestamp);
    const matchesMonth = d.getMonth() === currentMonth.getMonth() && d.getFullYear() === currentMonth.getFullYear();
    const matchesDate = selectedDate ? d.getDate() === selectedDate.getDate() : true;
    const matchesSearch = searchQuery 
        ? record.name.toLowerCase().includes(searchQuery.toLowerCase()) || record.brand.toLowerCase().includes(searchQuery.toLowerCase())
        : true;

    if (searchQuery) return matchesSearch;
    return matchesMonth && matchesDate;
  }).sort((a,b) => b.timestamp - a.timestamp);

  // Group records for calendar dots (visual only)
  const recordsByDay = records.reduce((acc, record) => {
    const d = new Date(record.timestamp);
    if (d.getMonth() === currentMonth.getMonth() && d.getFullYear() === currentMonth.getFullYear()) {
      const day = d.getDate();
      if (!acc[day]) acc[day] = [];
      acc[day].push(record);
    }
    return acc;
  }, {} as Record<number, TeaRecord[]>);

  const renderStars = (rating: number) => {
    return "★".repeat(rating) + "☆".repeat(5 - rating);
  };

  return (
    <div className="p-6 max-w-md mx-auto animate-fade-in">
        <header className="mb-6 space-y-4">
            <h1 className="text-3xl font-bold text-milk-tea-900">Tea Journal</h1>

            {/* Search Bar */}
            <div className="relative z-10">
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-milk-tea-300" size={18} />
                <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    placeholder="Search your history..."
                    className="w-full pl-10 pr-4 py-2.5 bg-white rounded-xl border border-milk-tea-100 text-sm text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-200 placeholder:text-milk-tea-200 transition-all shadow-sm"
                />
                {searchQuery && (
                    <button 
                    onClick={() => setSearchQuery('')}
                    className="absolute right-3 top-1/2 -translate-y-1/2 text-milk-tea-300 hover:text-milk-tea-500 p-1"
                    >
                    <X size={14} />
                    </button>
                )}
            </div>

            {/* Month Nav - Hidden if searching */}
            {!searchQuery && (
                <div className="relative flex items-center justify-center bg-white/50 p-2 rounded-2xl backdrop-blur-sm z-20 min-h-[56px]">
                    <button onClick={prevMonth} className="absolute left-2 p-2 text-milk-tea-600 hover:bg-white rounded-xl transition-colors">
                        <ChevronLeft size={20} />
                    </button>

                    {/* Centered Date Picker Button */}
                    <button 
                        onClick={() => setIsDatePickerOpen(!isDatePickerOpen)}
                        className="flex items-center gap-2 px-4 py-1.5 bg-milk-tea-50/50 rounded-full cursor-pointer hover:bg-milk-tea-100 transition-colors"
                    >
                        <span className="text-milk-tea-800 font-bold text-sm uppercase tracking-widest">
                            {currentMonth.toLocaleString('default', { month: 'long', year: 'numeric' })}
                        </span>
                        <ChevronDown size={16} className={`text-milk-tea-400 transition-transform duration-300 ${isDatePickerOpen ? 'rotate-180' : ''}`} />
                    </button>

                    {/* Custom Date Picker Popup - Centered relative to the header container */}
                    {isDatePickerOpen && (
                        <>
                            {/* Invisible backdrop to close on outside click */}
                            <div className="fixed inset-0 z-40" onClick={() => setIsDatePickerOpen(false)} />

                            <div className="absolute top-full mt-2 left-1/2 -translate-x-1/2 bg-white/95 backdrop-blur-xl p-3 rounded-2xl shadow-[0_10px_40px_rgba(0,0,0,0.12)] border border-white/50 z-50 flex gap-2 min-w-[240px] animate-fade-in origin-top" onClick={(e) => e.stopPropagation()}>
                                <div className="relative flex-1">
                                    <select 
                                        value={currentMonth.getFullYear()}
                                        onChange={(e) => {
                                            const newYear = parseInt(e.target.value);
                                            setCurrentMonth(new Date(newYear, currentMonth.getMonth(), 1));
                                        }}
                                        className="w-full p-2 bg-milk-tea-50 rounded-xl text-sm font-bold text-milk-tea-900 focus:outline-none appearance-none text-center hover:bg-milk-tea-100 transition-colors cursor-pointer"
                                    >
                                        {years.map(y => <option key={y} value={y}>{y}</option>)}
                                    </select>
                                    <div className="absolute inset-y-0 right-1 flex items-center pointer-events-none">
                                        <ChevronDown size={12} className="text-milk-tea-400" />
                                    </div>
                                </div>

                                <div className="relative flex-1">
                                    <select 
                                        value={currentMonth.getMonth()}
                                        onChange={(e) => {
                                            const newMonth = parseInt(e.target.value);
                                            setCurrentMonth(new Date(currentMonth.getFullYear(), newMonth, 1));
                                        }}
                                        className="w-full p-2 bg-milk-tea-50 rounded-xl text-sm font-bold text-milk-tea-900 focus:outline-none appearance-none text-center hover:bg-milk-tea-100 transition-colors cursor-pointer"
                                    >
                                        {months.map(m => (
                                            <option key={m} value={m}>
                                                {new Date(2000, m, 1).toLocaleString('default', { month: 'short' })}
                                            </option>
                                        ))}
                                    </select>
                                    <div className="absolute inset-y-0 right-1 flex items-center pointer-events-none">
                                        <ChevronDown size={12} className="text-milk-tea-400" />
                                    </div>
                                </div>
                            </div>
                        </>
                    )}

                    <button onClick={nextMonth} className="absolute right-2 p-2 text-milk-tea-600 hover:bg-white rounded-xl transition-colors">
                        <ChevronRight size={20} />
                    </button>
                </div>
            )}
        </header>

      {/* Calendar Grid - Hidden if searching */}
      {!searchQuery && (
          <div className="bg-white rounded-3xl p-6 shadow-sm shadow-milk-tea-100 mb-8 transition-all relative z-0">
            <div className="grid grid-cols-7 gap-2 mb-4">
              {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map(d => (
                <div key={d} className="text-center text-xs text-milk-tea-400 font-bold">{d}</div>
              ))}
            </div>
            <div className="grid grid-cols-7 gap-y-4 gap-x-2">
              {Array.from({ length: firstDay }).map((_, i) => (
                <div key={`empty-${i}`} />
              ))}
              {Array.from({ length: daysInMonth }).map((_, i) => {
                const day = i + 1;
                const dailyRecords = recordsByDay[day] || [];
                const count = dailyRecords.length;
                const hasTea = count > 0;
                const isSelected = selectedDate?.getDate() === day && selectedDate?.getMonth() === currentMonth.getMonth();

                // Determine background color based on intensity
                let bgClass = '';
                let textClass = '';

                if (isSelected) {
                   bgClass = 'bg-milk-tea-800 shadow-md scale-110';
                   textClass = 'text-white';
                } else if (count === 0) {
                   bgClass = 'text-milk-tea-300 hover:bg-cream';
                   textClass = 'text-milk-tea-300';
                } else if (count === 1) {
                   bgClass = 'bg-milk-tea-100 hover:bg-milk-tea-200';
                   textClass = 'text-milk-tea-800';
                } else if (count === 2) {
                   bgClass = 'bg-milk-tea-200 hover:bg-[#EBCFBF]';
                   textClass = 'text-milk-tea-900';
                } else {
                   // 3 or more - Use a darker beige/pink but keep text dark to allow dark dots
                   bgClass = 'bg-[#Dbb0a0] hover:bg-[#cf9f8f]';
                   textClass = 'text-milk-tea-900';
                }

                // Dot Logic: Max 3 dots
                const dotsCount = Math.min(count, 3);

                // Only if background is dark brown (selected) do we use white dots.
                // For 3+ cups (bg-[#Dbb0a0]), we keep dark dots for consistency.
                const isDarkBg = isSelected;
                const dotColor = isDarkBg ? 'bg-white/90' : 'bg-milk-tea-800/70';

                return (
                  <button 
                    key={day} 
                    onClick={() => {
                        // Toggle selection
                        if (isSelected) setSelectedDate(null);
                        else {
                            const newDate = new Date(currentMonth);
                            newDate.setDate(day);
                            setSelectedDate(newDate);
                        }
                    }}
                    className="flex flex-col items-center gap-1 group relative"
                  >
                    <span className={`text-sm font-medium w-8 h-8 flex items-center justify-center rounded-full transition-all duration-300 ${bgClass} ${textClass}`}>
                      {day}
                    </span>
                    {/* Dots always visible if hasTea */}
                    {hasTea && (
                        <div className="flex gap-0.5 absolute -bottom-1.5">
                            {Array.from({ length: dotsCount }).map((_, idx) => {
                                return (
                                    <div key={idx} className={`w-1 h-1 rounded-full ${dotColor}`} />
                                );
                            })}
                        </div>
                    )}
                  </button>
                );
              })}
            </div>
          </div>
      )}

      {/* Filtered Records List */}
      <div className="flex justify-between items-center mb-4 px-1">
          <h3 className="text-lg font-bold text-milk-tea-800">
            {searchQuery ? 'Search Results' : selectedDate ? 'Daily Sips' : 'Recent Sips'}
          </h3>
          {selectedDate && !searchQuery && (
              <button onClick={() => setSelectedDate(null)} className="text-xs text-milk-tea-500 hover:text-milk-tea-800">Clear Date</button>
          )}
      </div>

      <div className="space-y-4 pb-24">
        {filteredRecords.map(record => (
          <div 
            key={record.id} 
            onClick={() => onViewRecord?.(record)}
            className="bg-white p-4 rounded-2xl flex gap-4 shadow-sm shadow-milk-tea-100 border border-transparent hover:border-milk-tea-200 transition-colors group relative cursor-pointer"
          >
            <div className="w-16 h-16 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative">
                {record.imageUrl ? (
                    <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                ) : (
                    <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-2xl font-bold opacity-50">
                        {record.brand[0]}
                    </div>
                )}
            </div>
            <div className="flex-1 min-w-0">
              <div className="flex justify-between items-start">
                <div>
                    <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                    <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                </div>
                <div className="flex flex-col items-end">
                    <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
                    <span className="text-xs font-bold text-milk-tea-800 mt-1">¥{record.price}</span>
                </div>
              </div>

              <div className="mt-2 flex flex-wrap gap-2">
                <span className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                    {record.sugar || 'Regular'}
                </span>
                {record.tags && record.tags.map((tag, i) => (
                    <span key={i} className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                        {tag}
                    </span>
                ))}
              </div>
            </div>

            {/* Action Buttons (High Z-Index to fix clicking issues) */}
            <div className="absolute top-2 right-2 flex gap-2 bg-white/60 backdrop-blur rounded-lg p-1 z-50 border border-white/20 shadow-sm" onClick={(e) => e.stopPropagation()}>
                <button 
                    onClick={(e) => { 
                        e.stopPropagation(); 
                        if (onEdit) onEdit(record); 
                    }} 
                    className="p-1.5 text-milk-tea-500 hover:text-milk-tea-800 hover:bg-milk-tea-100 rounded-md transition-colors"
                    aria-label="Edit"
                >
                    <Edit2 size={14} className="pointer-events-none" />
                </button>
                <button 
                    onClick={(e) => { 
                        e.stopPropagation(); 
                        if (onDelete) onDelete(record.id); 
                    }} 
                    className="p-1.5 text-red-400 hover:text-red-600 hover:bg-red-50 rounded-md transition-colors"
                    aria-label="Delete"
                >
                    <Trash2 size={14} className="pointer-events-none" />
                </button>
            </div>
          </div>
        ))}
        {filteredRecords.length === 0 && (
            <div className="text-center py-12 text-milk-tea-300">
                <p>{searchQuery ? 'No matches found.' : 'No bubble tea recorded.'}</p>
            </div>
        )}
      </div>
    </div>
  );
};

import React, { useMemo, useState } from 'react';
import { TeaRecord } from '../types';
import { Search, X, ChevronDown, CupSoda } from 'lucide-react';

interface LibraryViewProps {
  records: TeaRecord[];
}

interface DrinkGroup {
    name: string;
    records: TeaRecord[];
    count: number;
    avgRating: number;
    latestTimestamp: number;
}

interface BrandGroup {
    brand: string;
    drinks: DrinkGroup[];
    totalCount: number;
    avgRating: number;
    latestTimestamp: number;
}

type SortOption = 'frequency' | 'rating' | 'date';

export const LibraryView: React.FC<LibraryViewProps> = ({ records }) => {
  const [searchQuery, setSearchQuery] = useState('');
  // Changed to Set to support multiple expanded brands
  const [expandedBrands, setExpandedBrands] = useState<Set<string>>(new Set());
  const [sortBy, setSortBy] = useState<SortOption>('frequency');

  // Grouping Logic
  const groupedData: BrandGroup[] = useMemo(() => {
    if (searchQuery) return []; 

    const brands: Record<string, Record<string, TeaRecord[]>> = {};

    // 1. Group by Brand -> Drink Name
    records.forEach(r => {
        if (!brands[r.brand]) brands[r.brand] = {};
        if (!brands[r.brand][r.name]) brands[r.brand][r.name] = [];
        brands[r.brand][r.name].push(r);
    });

    // 2. Transform to Array structure
    const brandGroups: BrandGroup[] = Object.entries(brands).map(([brandName, drinkMap]) => {
        const drinks: DrinkGroup[] = Object.entries(drinkMap).map(([drinkName, drinkRecords]) => {
            const sumRating = drinkRecords.reduce((sum, r) => sum + r.rating, 0);
            const latest = Math.max(...drinkRecords.map(r => r.timestamp));

            return {
                name: drinkName,
                records: drinkRecords,
                count: drinkRecords.length,
                avgRating: sumRating / drinkRecords.length,
                latestTimestamp: latest
            };
        });

        // Sort Drinks inside the brand based on current sort option
        drinks.sort((a, b) => {
            if (sortBy === 'frequency') {
                if (b.count !== a.count) return b.count - a.count;
                return b.latestTimestamp - a.latestTimestamp;
            } else if (sortBy === 'rating') {
                if (b.avgRating !== a.avgRating) return b.avgRating - a.avgRating;
                return b.latestTimestamp - a.latestTimestamp;
            } else { // date
                return b.latestTimestamp - a.latestTimestamp;
            }
        });

        const totalCount = drinks.reduce((sum, d) => sum + d.count, 0);

        // Calculate weighted average for brand
        const allBrandRecords = drinks.flatMap(d => d.records);
        const brandAvgRating = allBrandRecords.reduce((sum, r) => sum + r.rating, 0) / allBrandRecords.length;

        // Find latest timestamp for the brand
        const brandLatestTimestamp = Math.max(...drinks.map(d => d.latestTimestamp));

        return {
            brand: brandName,
            drinks,
            totalCount,
            avgRating: brandAvgRating,
            latestTimestamp: brandLatestTimestamp
        };
    });

    // 3. Sort Brands based on the selected criteria
    brandGroups.sort((a, b) => {
        if (sortBy === 'frequency') {
            // Sort by total count desc
            if (b.totalCount !== a.totalCount) return b.totalCount - a.totalCount;
            return a.brand.localeCompare(b.brand);
        } else if (sortBy === 'rating') {
            // Sort by average rating desc
            if (b.avgRating !== a.avgRating) return b.avgRating - a.avgRating;
            return a.brand.localeCompare(b.brand);
        } else { // date
            // Sort by latest timestamp desc
            return b.latestTimestamp - a.latestTimestamp;
        }
    });

    return brandGroups;
  }, [records, searchQuery, sortBy]);

  // Search Logic
  const filteredRecords = useMemo(() => {
    if (!searchQuery.trim()) return [];
    const lowerQuery = searchQuery.toLowerCase();
    return records.filter(
      r => 
        r.brand.toLowerCase().includes(lowerQuery) || 
        r.name.toLowerCase().includes(lowerQuery)
    );
  }, [records, searchQuery]);

  const renderStars = (rating: number) => {
    return "★".repeat(Math.round(rating)) + "☆".repeat(5 - Math.round(rating));
  };

  const toggleBrand = (brand: string) => {
      const newSet = new Set(expandedBrands);
      if (newSet.has(brand)) {
          newSet.delete(brand);
      } else {
          newSet.add(brand);
      }
      setExpandedBrands(newSet);
  };

  const SortButton = ({ type, label }: { type: SortOption, label: string }) => (
    <button 
        onClick={() => setSortBy(type)}
        className={`px-4 py-1.5 rounded-full text-xs font-bold transition-all border ${
            sortBy === type 
                ? 'bg-milk-tea-800 text-white border-milk-tea-800' 
                : 'bg-white text-milk-tea-500 border-milk-tea-100 hover:bg-milk-tea-50'
        }`}
    >
        {label}
    </button>
  );

  return (
    <div className="p-6 pb-24 max-w-md mx-auto animate-fade-in">
      <div className="sticky top-0 bg-cream/95 backdrop-blur-sm z-10 pb-4 -mx-6 px-6 pt-2">
        <h1 className="text-3xl font-bold text-milk-tea-900 mb-4">Tasting Log</h1>

        {/* Search Bar */}
        <div className="relative mb-3">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-milk-tea-300" size={20} />
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search brand or drink..."
            className="w-full pl-12 pr-10 py-3 bg-white rounded-2xl border border-milk-tea-100 text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-200 placeholder:text-milk-tea-200 transition-all shadow-sm"
          />
          {searchQuery && (
            <button 
              onClick={() => setSearchQuery('')}
              className="absolute right-3 top-1/2 -translate-y-1/2 text-milk-tea-300 hover:text-milk-tea-500 p-1"
            >
              <X size={16} />
            </button>
          )}
        </div>

        {/* Sort Controls (Only visible when not searching) */}
        {!searchQuery && (
            <div className="flex gap-2 overflow-x-auto no-scrollbar pb-1">
                {/* Icon removed here */}
                <SortButton type="frequency" label="Most Frequent" />
                <SortButton type="rating" label="Highest Rated" />
                <SortButton type="date" label="Newest" />
            </div>
        )}
      </div>

      <div className="space-y-4 mt-2">
        {/* Accordion Brand List */}
        {!searchQuery && groupedData.map((group) => {
            const isExpanded = expandedBrands.has(group.brand);
            return (
                <div key={group.brand} className="bg-white rounded-3xl overflow-hidden shadow-sm border border-milk-tea-100 transition-all duration-300">
                    <button 
                        onClick={() => toggleBrand(group.brand)}
                        className={`w-full flex justify-between items-center p-5 text-left transition-colors ${isExpanded ? 'bg-milk-tea-50/50' : 'hover:bg-milk-tea-50/30'}`}
                    >
                        <div className="flex items-center gap-4">
                             <div className="w-12 h-12 rounded-full bg-milk-tea-100 flex items-center justify-center text-milk-tea-800 font-bold text-xl">
                                {group.brand[0]}
                             </div>
                             <div>
                                <h2 className="font-bold text-milk-tea-900 text-lg">{group.brand}</h2>
                                <p className="text-xs text-milk-tea-500 font-medium">
                                    {group.totalCount} drinks • {group.avgRating.toFixed(1)} avg
                                </p>
                             </div>
                        </div>
                        <div className={`text-milk-tea-400 transition-transform duration-300 ${isExpanded ? 'rotate-180' : ''}`}>
                            <ChevronDown size={20} />
                        </div>
                    </button>

                    {/* Expanded Content */}
                    {isExpanded && (
                        <div className="bg-white border-t border-milk-tea-50 animate-fade-in">
                            {group.drinks.map((drink, idx) => (
                                <div key={`${drink.name}-${idx}`} className="p-4 flex items-center justify-between border-b border-milk-tea-50 last:border-none hover:bg-cream/50 transition-colors">
                                    <div className="flex items-start gap-3">
                                        <div className="mt-1 text-milk-tea-300">
                                            <CupSoda size={16} />
                                        </div>
                                        <div>
                                            <p className="font-bold text-milk-tea-800 text-sm">{drink.name}</p>
                                            <div className="flex gap-2 mt-1">
                                                <span className="px-1.5 py-0.5 bg-milk-tea-100 text-milk-tea-800 text-[10px] rounded font-medium">
                                                    {drink.count} cups
                                                </span>
                                                <span className="text-[10px] text-milk-tea-400 flex items-center">
                                                    Last: {new Date(drink.latestTimestamp).toLocaleDateString()}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="text-right">
                                        <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(drink.avgRating)}</span>
                                        <p className="text-[10px] text-milk-tea-400 font-medium mt-0.5">
                                            {drink.avgRating.toFixed(1)}
                                        </p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            )
        })}

        {/* Search Results (Flat List) */}
        {searchQuery && filteredRecords.map(record => (
             <div key={record.id} className="bg-white p-4 rounded-2xl flex gap-4 shadow-sm shadow-milk-tea-100 border border-transparent">
                <div className="w-14 h-14 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative">
                    {record.imageUrl ? (
                        <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                    ) : (
                        <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-xl font-bold opacity-50">
                            {record.brand[0]}
                        </div>
                    )}
                </div>
                <div className="flex-1 min-w-0">
                    <div className="flex justify-between items-start">
                        <div>
                            <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                            <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                        </div>
                        <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
                    </div>
                    <div className="mt-1 flex flex-wrap gap-2">
                         <p className="text-[11px] text-milk-tea-400">
                             {record.sugar} • {record.ice}
                         </p>
                    </div>
                </div>
             </div>
        ))}

        {records.length > 0 && searchQuery && filteredRecords.length === 0 && (
             <div className="text-center text-milk-tea-300 mt-10">
                <p>No matches found for "{searchQuery}"</p>
            </div>
        )}

        {records.length === 0 && (
            <div className="text-center text-milk-tea-300 mt-20 flex flex-col items-center">
                <p>Your library is empty.</p>
                <p className="text-sm">Start drinking!</p>
            </div>
        )}
      </div>
    </div>
  );
};

import React, { useState, useRef } from 'react';
import { TeaRecord } from '../types';
import { ChevronLeft, ChevronRight, Edit2, Trash2, Search, X, ChevronDown } from 'lucide-react';

interface CalendarViewProps {
  records: TeaRecord[];
  onDelete?: (id: string) => void;
  onEdit?: (record: TeaRecord) => void;
  onViewRecord?: (record: TeaRecord) => void;
}

const renderStars = (rating: number) => {
  return "★".repeat(rating) + "☆".repeat(5 - rating);
};

// Swipeable Item Component
interface SwipeableRecordItemProps {
    record: TeaRecord;
    onEdit: (record: TeaRecord) => void;
    onDelete: (id: string) => void;
    onView: (record: TeaRecord) => void;
}

const SwipeableRecordItem: React.FC<SwipeableRecordItemProps> = ({ record, onEdit, onDelete, onView }) => {
    const [offsetX, setOffsetX] = useState(0);
    const startXRef = useRef<number | null>(null);

    const handleTouchStart = (e: React.TouchEvent) => {
        startXRef.current = e.touches[0].clientX;
    };

    const handleTouchMove = (e: React.TouchEvent) => {
        if (startXRef.current === null) return;
        const currentX = e.touches[0].clientX;
        const diff = currentX - startXRef.current;

        // Only allow swiping left (negative)
        // Limit max swipe to -140 (width of buttons)
        if (diff < 0) {
            setOffsetX(Math.max(diff, -140));
        } else {
             // Elastic bounce back if swiping right
             setOffsetX(Math.min(0, Math.max(diff - 140, -140)));
        }
    };

    const handleTouchEnd = () => {
        // Threshold to snap open
        if (offsetX < -70) {
            setOffsetX(-140);
        } else {
            setOffsetX(0);
        }
        startXRef.current = null;
    };

    return (
        <div className="relative group rounded-2xl shadow-sm shadow-milk-tea-100 mb-4 select-none bg-milk-tea-100/50 overflow-hidden">
             {/* Action Buttons Background */}
             <div className="absolute inset-y-0 right-0 w-[140px] flex rounded-r-2xl">
                <button
                    onClick={(e) => {
                         e.stopPropagation();
                         onEdit(record);
                         setOffsetX(0); 
                    }}
                    className="flex-1 bg-milk-tea-200 text-milk-tea-800 flex flex-col items-center justify-center gap-1 active:bg-milk-tea-300 transition-colors"
                >
                    <Edit2 size={18} />
                    <span className="text-[10px] font-bold">Edit</span>
                </button>
                <button
                    onClick={(e) => {
                        e.stopPropagation();
                        onDelete(record.id);
                        setOffsetX(0);
                    }}
                    className="flex-1 bg-red-400 text-white flex flex-col items-center justify-center gap-1 active:bg-red-500 transition-colors"
                >
                    <Trash2 size={18} />
                    <span className="text-[10px] font-bold">Del</span>
                </button>
             </div>

             {/* Main Card Content */}
             <div 
                className="relative bg-white p-4 rounded-2xl flex gap-4 border border-transparent transition-transform duration-300 ease-out z-10"
                style={{ transform: `translateX(${offsetX}px)` }}
                onTouchStart={handleTouchStart}
                onTouchMove={handleTouchMove}
                onTouchEnd={handleTouchEnd}
                onClick={() => {
                    if (offsetX < -10) setOffsetX(0); // Close if open
                    else onView(record);
                }}
             >
                <div className="w-16 h-16 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative pointer-events-none">
                    {record.imageUrl ? (
                        <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                    ) : (
                        <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-2xl font-bold opacity-50">
                            {record.brand[0]}
                        </div>
                    )}
                </div>
                <div className="flex-1 min-w-0 pointer-events-none">
                    <div className="flex justify-between items-start">
                        <div>
                            <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                            <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                        </div>
                        <div className="flex flex-col items-end">
                            <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
                            <span className="text-xs font-bold text-milk-tea-800 mt-1">¥{record.price}</span>
                        </div>
                    </div>

                    <div className="mt-2 flex flex-wrap gap-2">
                        <span className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                            {record.sugar || 'Regular'}
                        </span>
                        {record.tags && record.tags.map((tag, i) => (
                            <span key={i} className="px-2 py-0.5 bg-milk-tea-50 text-milk-tea-600 text-[10px] rounded-md border border-milk-tea-100">
                                {tag}
                            </span>
                        ))}
                    </div>
                </div>
             </div>
        </div>
    );
};

export const CalendarView: React.FC<CalendarViewProps> = ({ records, onDelete, onEdit, onViewRecord }) => {
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isDatePickerOpen, setIsDatePickerOpen] = useState(false);

  // Month navigation
  const prevMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  const nextMonth = () => setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));

  // Dropdown data
  const currentYear = new Date().getFullYear();
  const years = Array.from({ length: 11 }, (_, i) => currentYear - 5 + i); // +/- 5 years
  const months = Array.from({ length: 12 }, (_, i) => i);

  const getDaysInMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
  };

  const daysInMonth = getDaysInMonth(currentMonth);
  const firstDay = getFirstDayOfMonth(currentMonth);

  // Filter records
  const filteredRecords = records.filter(record => {
    const d = new Date(record.timestamp);
    const matchesMonth = d.getMonth() === currentMonth.getMonth() && d.getFullYear() === currentMonth.getFullYear();
