import { GoogleGenAI, Type } from "@google/genai";
import { TeaRecord } from "../types";

// This will fail if process.env.API_KEY is not set, as per instructions.
// The component using this must handle the error gracefully.
const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

export interface ExtractedTeaInfo {
  brand?: string;
  name?: string;
  sugar?: string;
  ice?: string;
  price?: number;
}

export const analyzeTeaImage = async (base64Image: string): Promise<ExtractedTeaInfo> => {
  try {
    // Extract mime type dynamically or default to jpeg
    const mimeMatch = base64Image.match(/^data:(image\/[a-zA-Z]+);base64,/);
    const mimeType = mimeMatch ? mimeMatch[1] : 'image/jpeg';

    // Remove the data URL prefix to get just the base64 string
    const cleanBase64 = base64Image.replace(/^data:image\/[a-zA-Z]+;base64,/, "");

    // Use gemini-3-flash-preview for multimodal analysis with JSON support
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: {
        parts: [
          {
            inlineData: {
              mimeType: mimeType,
              data: cleanBase64
            }
          },
          {
            text: "Identify the bubble tea information from this image (receipt, label, or cup). Return a JSON object with: brand, name, sugar_level, ice_level, price. Translate all extracted text to Simplified Chinese. If a field is missing, omit it or use null. Keep strings short and concise."
          }
        ]
      },
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            brand: { type: Type.STRING },
            name: { type: Type.STRING },
            sugar_level: { type: Type.STRING },
            ice_level: { type: Type.STRING },
            price: { type: Type.NUMBER },
          }
        }
      }
    });

    const jsonText = response.text;
    if (!jsonText) throw new Error("No response from AI");

    const data = JSON.parse(jsonText);

    return {
      brand: data.brand,
      name: data.name,
      sugar: data.sugar_level,
      ice: data.ice_level,
      price: data.price
    };

  } catch (error) {
    console.error("Gemini analysis failed:", error);
    throw error;
  }
};

import React, { useState, useRef, useEffect } from 'react';
import { Camera, X, Loader2, Sparkles, Upload, Image as ImageIcon, ScanText } from 'lucide-react';
import { TeaRecord } from '../types';
import { analyzeTeaImage } from '../services/geminiService';

interface RecordFormProps {
  initialData?: TeaRecord;
  onSave: (record: Omit<TeaRecord, 'id' | 'timestamp'>) => void;
  onCancel: () => void;
}

const COMMON_SUGAR = ['标准', '七分糖', '五分糖', '三分糖', '无糖'];
const COMMON_ICE = ['正常冰', '少冰', '去冰', '温', '热饮'];

export const RecordForm: React.FC<RecordFormProps> = ({ initialData, onSave, onCancel }) => {
  const [loading, setLoading] = useState(false);
  const [imagePreview, setImagePreview] = useState<string | null>(initialData?.imageUrl || null);

  const [formData, setFormData] = useState({
    brand: initialData?.brand || '',
    name: initialData?.name || '',
    sugar: initialData?.sugar || '',
    ice: initialData?.ice || '',
    price: initialData?.price ? String(initialData.price) : '',
    rating: initialData?.rating || 0,
    remark: initialData?.remark || '',
  });

  const [errors, setErrors] = useState({
      brand: false,
      name: false
  });

  // Tag/Topping state
  const [currentTag, setCurrentTag] = useState('');
  const [tags, setTags] = useState<string[]>(initialData?.tags || []);

  const coverInputRef = useRef<HTMLInputElement>(null);
  const aiInputRef = useRef<HTMLInputElement>(null);

  // Handle Cover Photo (Visual Only)
  const handleCoverChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = async () => {
      const base64 = reader.result as string;
      setImagePreview(base64); 
    };
    reader.readAsDataURL(file);
  };

  // Handle AI Analysis (Data Fill Only)
  const handleAIAnalysis = async (e: React.ChangeEvent<HTMLInputElement>) => {
      const file = e.target.files?.[0];
      if (!file) return;

      setLoading(true);
      const reader = new FileReader();
      reader.onloadend = async () => {
        const base64 = reader.result as string;
        try {
            const info = await analyzeTeaImage(base64);
            setFormData(prev => ({
                ...prev,
                brand: info.brand || prev.brand,
                name: info.name || prev.name,
                sugar: info.sugar || prev.sugar,
                ice: info.ice || prev.ice,
                price: info.price ? String(info.price) : prev.price
            }));

            // Clear errors if AI finds data
            setErrors(prev => ({
                brand: info.brand ? false : prev.brand,
                name: info.name ? false : prev.name
            }));
        } catch (err) {
            console.error("AI Analysis failed", err);
            // Optional: Show error toast
        } finally {
            setLoading(false);
            // Reset input so same file can be selected again if needed
            if (aiInputRef.current) aiInputRef.current.value = '';
        }
      };
      reader.readAsDataURL(file);
  };

  const handleTagKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
        e.preventDefault();
        if (currentTag.trim()) {
            setTags([...tags, currentTag.trim()]);
            setCurrentTag('');
        }
    }
  };

  const removeTag = (index: number) => {
    setTags(tags.filter((_, i) => i !== index));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Validation
    const newErrors = {
        brand: !formData.brand.trim(),
        name: !formData.name.trim()
    };

    if (newErrors.brand || newErrors.name) {
        setErrors(newErrors);
        return;
    }

    onSave({
      brand: formData.brand.trim(),
      name: formData.name.trim(),
      sugar: formData.sugar,
      ice: formData.ice,
      price: parseFloat(formData.price) || 0,
      rating: formData.rating,
      remark: formData.remark,
      imageUrl: imagePreview || undefined,
      tags: tags
    });
  };

  return (
    <div className="bg-cream min-h-full pb-6 animate-fade-in">
      <div className="sticky top-0 bg-cream/95 backdrop-blur z-20 px-6 py-4 flex justify-between items-center border-b border-milk-tea-100">
        <button onClick={onCancel} className="p-2 -ml-2 text-milk-tea-500">
            <X size={24} />
        </button>
        <h2 className="text-lg font-bold text-milk-tea-900">{initialData ? '编辑记录' : '记一杯'}</h2>
        <button 
            onClick={handleSubmit}
            className="px-4 py-1.5 bg-milk-tea-800 text-cream rounded-full text-sm font-semibold shadow-md active:scale-95 transition-transform"
        >
            保存
        </button>
      </div>

      <div className="p-6 space-y-6 max-w-md mx-auto">
        {/* Cover Image Section */}
        <div className="relative group">
            <input 
                type="file" 
                ref={coverInputRef} 
                onChange={handleCoverChange} 
                accept="image/png, image/jpeg, image/jpg, image/webp" 
                className="hidden" 
            />

            <div 
                onClick={() => coverInputRef.current?.click()}
                className={`w-full aspect-[4/3] rounded-3xl border-2 border-dashed flex flex-col items-center justify-center cursor-pointer transition-all overflow-hidden ${
                    imagePreview ? 'border-transparent' : 'border-milk-tea-200 bg-milk-tea-100/30 hover:bg-milk-tea-100/50'
                }`}
            >
                {imagePreview ? (
                    <>
                        <img src={imagePreview} alt="Preview" className="w-full h-full object-cover" />
                        <div className="absolute inset-0 bg-black/20 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                            <span className="text-white text-sm font-medium flex items-center gap-2">
                                <ImageIcon size={16} /> 更换封面
                            </span>
                        </div>
                    </>
                ) : (
                    <div className="text-milk-tea-400 flex flex-col items-center gap-2">
                        <ImageIcon size={32} />
                        <span className="text-sm font-medium">添加封面图片</span>
                    </div>
                )}
            </div>
        </div>

        {/* AI Smart Fill Section */}
        <div className="bg-white p-3 rounded-2xl border border-milk-tea-100 shadow-sm flex items-center gap-3">
            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-indigo-100 to-purple-100 flex items-center justify-center text-indigo-500">
                <Sparkles size={18} />
            </div>
            <div className="flex-1">
                <p className="text-xs font-bold text-milk-tea-800">智能识别</p>
                <p className="text-[10px] text-milk-tea-400">上传小票或标签自动填写</p>
            </div>

            <input 
                type="file" 
