             </div>
             <div className="flex-1 bg-white p-4 rounded-2xl shadow-sm border border-milk-tea-100">
                 <p className="text-xs text-milk-tea-400 uppercase font-bold">平均评分</p>
                 <p className="text-2xl font-bold text-milk-tea-800">{avgRating}<span className="text-sm text-milk-tea-400">/5</span></p>
             </div>
         </div>
      </header>

      {/* Brand Chart */}
      <section className="bg-white p-6 rounded-3xl shadow-sm border border-milk-tea-100">
        <h3 className="text-lg font-bold text-milk-tea-900 mb-4">品牌偏好</h3>
        <div className="h-64 w-full">
            {brandData.length > 0 ? (
                <ResponsiveContainer width="100%" height="100%">
                    <PieChart>
                    <Pie
                        data={brandData}
                        cx="50%"
                        cy="50%"
                        innerRadius={60}
                        outerRadius={80}
                        paddingAngle={5}
                        dataKey="value"
                    >
                        {brandData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                        ))}
                    </Pie>
                    <Tooltip 
                        contentStyle={{ borderRadius: '12px', border: 'none', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }}
                    />
                    </PieChart>
                </ResponsiveContainer>
            ) : (
                <div className="h-full flex items-center justify-center text-milk-tea-300">暂无数据</div>
            )}
        </div>
        <div className="flex flex-wrap gap-2 justify-center mt-2">
            {brandData.map((b, i) => (
                <div key={b.name} className="flex items-center gap-1 text-xs text-milk-tea-600">
                    <span className="w-2 h-2 rounded-full" style={{backgroundColor: COLORS[i % COLORS.length]}} />
                    {b.name}
                </div>
            ))}
        </div>
      </section>

      {/* Weekly Frequency */}
      <section className="bg-white p-6 rounded-3xl shadow-sm border border-milk-tea-100">
        <h3 className="text-lg font-bold text-milk-tea-900 mb-4">本周趋势</h3>
        <div className="h-48 w-full">
             <ResponsiveContainer width="100%" height="100%">
                <BarChart data={last7Days}>
                    <XAxis 
                        dataKey="name" 
                        axisLine={false} 
                        tickLine={false} 
                        tick={{fill: '#9CA3AF', fontSize: 10}} 
                        dy={10}
                    />
                    <Tooltip 
                        cursor={{fill: '#FCEDE4', opacity: 0.4}}
                        contentStyle={{ borderRadius: '8px', border: 'none', boxShadow: '0 2px 8px rgba(0,0,0,0.05)' }}
                    />
                    <Bar dataKey="count" fill="#C89F8D" radius={[4, 4, 4, 4]} barSize={20} />
                </BarChart>
            </ResponsiveContainer>
        </div>
      </section>
    </div>
  );
};

import React, { useMemo, useState } from 'react';
import { TeaRecord } from '../types';
import { Search, X, ChevronDown, CupSoda } from 'lucide-react';

interface LibraryViewProps {
  records: TeaRecord[];
}

interface DrinkGroup {
    name: string;
    records: TeaRecord[];
    count: number;
    avgRating: number;
    latestTimestamp: number;
}

interface BrandGroup {
    brand: string;
    drinks: DrinkGroup[];
    totalCount: number;
    avgRating: number;
    latestTimestamp: number;
}

type SortOption = 'frequency' | 'rating' | 'date';

export const LibraryView: React.FC<LibraryViewProps> = ({ records }) => {
  const [searchQuery, setSearchQuery] = useState('');
  // Changed to Set to support multiple expanded brands
  const [expandedBrands, setExpandedBrands] = useState<Set<string>>(new Set());
  const [sortBy, setSortBy] = useState<SortOption>('frequency');

  // Grouping Logic
  const groupedData: BrandGroup[] = useMemo(() => {
    if (searchQuery) return []; 

    const brands: Record<string, Record<string, TeaRecord[]>> = {};

    // 1. Group by Brand -> Drink Name
    records.forEach(r => {
        if (!brands[r.brand]) brands[r.brand] = {};
        if (!brands[r.brand][r.name]) brands[r.brand][r.name] = [];
        brands[r.brand][r.name].push(r);
    });

    // 2. Transform to Array structure
    const brandGroups: BrandGroup[] = Object.entries(brands).map(([brandName, drinkMap]) => {
        const drinks: DrinkGroup[] = Object.entries(drinkMap).map(([drinkName, drinkRecords]) => {
            const sumRating = drinkRecords.reduce((sum, r) => sum + r.rating, 0);
            const latest = Math.max(...drinkRecords.map(r => r.timestamp));

            return {
                name: drinkName,
                records: drinkRecords,
                count: drinkRecords.length,
                avgRating: sumRating / drinkRecords.length,
                latestTimestamp: latest
            };
        });

        // Sort Drinks inside the brand based on current sort option
        drinks.sort((a, b) => {
            if (sortBy === 'frequency') {
                if (b.count !== a.count) return b.count - a.count;
                return b.latestTimestamp - a.latestTimestamp;
            } else if (sortBy === 'rating') {
                if (b.avgRating !== a.avgRating) return b.avgRating - a.avgRating;
                return b.latestTimestamp - a.latestTimestamp;
            } else { // date
                return b.latestTimestamp - a.latestTimestamp;
            }
        });

        const totalCount = drinks.reduce((sum, d) => sum + d.count, 0);

        // Calculate weighted average for brand
        const allBrandRecords = drinks.flatMap(d => d.records);
        const brandAvgRating = allBrandRecords.reduce((sum, r) => sum + r.rating, 0) / allBrandRecords.length;

        // Find latest timestamp for the brand
        const brandLatestTimestamp = Math.max(...drinks.map(d => d.latestTimestamp));

        return {
            brand: brandName,
            drinks,
            totalCount,
            avgRating: brandAvgRating,
            latestTimestamp: brandLatestTimestamp
        };
    });

    // 3. Sort Brands based on the selected criteria
    brandGroups.sort((a, b) => {
        if (sortBy === 'frequency') {
            // Sort by total count desc
            if (b.totalCount !== a.totalCount) return b.totalCount - a.totalCount;
            return a.brand.localeCompare(b.brand);
        } else if (sortBy === 'rating') {
            // Sort by average rating desc
            if (b.avgRating !== a.avgRating) return b.avgRating - a.avgRating;
            return a.brand.localeCompare(b.brand);
        } else { // date
            // Sort by latest timestamp desc
            return b.latestTimestamp - a.latestTimestamp;
        }
    });

    return brandGroups;
  }, [records, searchQuery, sortBy]);

  // Search Logic
  const filteredRecords = useMemo(() => {
    if (!searchQuery.trim()) return [];
    const lowerQuery = searchQuery.toLowerCase();
    return records.filter(
      r => 
        r.brand.toLowerCase().includes(lowerQuery) || 
        r.name.toLowerCase().includes(lowerQuery)
    );
  }, [records, searchQuery]);

  const renderStars = (rating: number) => {
    return "★".repeat(Math.round(rating)) + "☆".repeat(5 - Math.round(rating));
  };

  const toggleBrand = (brand: string) => {
      const newSet = new Set(expandedBrands);
      if (newSet.has(brand)) {
          newSet.delete(brand);
      } else {
          newSet.add(brand);
      }
      setExpandedBrands(newSet);
  };

  const SortButton = ({ type, label }: { type: SortOption, label: string }) => (
    <button 
        onClick={() => setSortBy(type)}
        className={`px-4 py-1.5 rounded-full text-xs font-bold transition-all border ${
            sortBy === type 
                ? 'bg-milk-tea-800 text-white border-milk-tea-800' 
                : 'bg-white text-milk-tea-500 border-milk-tea-100 hover:bg-milk-tea-50'
        }`}
    >
        {label}
    </button>
  );

  return (
    <div className="p-6 pb-24 max-w-md mx-auto animate-fade-in">
      <div className="sticky top-0 bg-cream/95 backdrop-blur-sm z-10 pb-4 -mx-6 px-6 pt-2">
        <h1 className="text-3xl font-bold text-milk-tea-900 mb-4">品鉴图鉴</h1>

        {/* Search Bar */}
        <div className="relative mb-3">
          <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-milk-tea-300" size={20} />
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="搜索品牌或饮品..."
            className="w-full pl-12 pr-10 py-3 bg-white rounded-2xl border border-milk-tea-100 text-milk-tea-900 focus:outline-none focus:ring-2 focus:ring-milk-tea-200 placeholder:text-milk-tea-200 transition-all shadow-sm"
          />
          {searchQuery && (
            <button 
              onClick={() => setSearchQuery('')}
              className="absolute right-3 top-1/2 -translate-y-1/2 text-milk-tea-300 hover:text-milk-tea-500 p-1"
            >
              <X size={16} />
            </button>
          )}
        </div>

        {/* Sort Controls (Only visible when not searching) */}
        {!searchQuery && (
            <div className="flex gap-2 overflow-x-auto no-scrollbar pb-1">
                {/* Icon removed here */}
                <SortButton type="frequency" label="最常喝" />
                <SortButton type="rating" label="评分最高" />
                <SortButton type="date" label="最新" />
            </div>
        )}
      </div>

      <div className="space-y-4 mt-2">
        {/* Accordion Brand List */}
        {!searchQuery && groupedData.map((group) => {
            const isExpanded = expandedBrands.has(group.brand);
            return (
                <div key={group.brand} className="bg-white rounded-3xl overflow-hidden shadow-sm border border-milk-tea-100 transition-all duration-300">
                    <button 
                        onClick={() => toggleBrand(group.brand)}
                        className={`w-full flex justify-between items-center p-5 text-left transition-colors ${isExpanded ? 'bg-milk-tea-50/50' : 'hover:bg-milk-tea-50/30'}`}
                    >
                        <div className="flex items-center gap-4">
                             <div className="w-12 h-12 rounded-full bg-milk-tea-100 flex items-center justify-center text-milk-tea-800 font-bold text-xl">
                                {group.brand[0]}
                             </div>
                             <div>
                                <h2 className="font-bold text-milk-tea-900 text-lg">{group.brand}</h2>
                                <p className="text-xs text-milk-tea-500 font-medium">
                                    {group.totalCount} 杯 • {group.avgRating.toFixed(1)} 平均分
                                </p>
                             </div>
                        </div>
                        <div className={`text-milk-tea-400 transition-transform duration-300 ${isExpanded ? 'rotate-180' : ''}`}>
                            <ChevronDown size={20} />
                        </div>
                    </button>

                    {/* Expanded Content */}
                    {isExpanded && (
                        <div className="bg-white border-t border-milk-tea-50 animate-fade-in">
                            {group.drinks.map((drink, idx) => (
                                <div key={`${drink.name}-${idx}`} className="p-4 flex items-center justify-between border-b border-milk-tea-50 last:border-none hover:bg-cream/50 transition-colors">
                                    <div className="flex items-start gap-3">
                                        <div className="mt-1 text-milk-tea-300">
                                            <CupSoda size={16} />
                                        </div>
                                        <div>
                                            <p className="font-bold text-milk-tea-800 text-sm">{drink.name}</p>
                                            <div className="flex gap-2 mt-1">
                                                <span className="px-1.5 py-0.5 bg-milk-tea-100 text-milk-tea-800 text-[10px] rounded font-medium">
                                                    {drink.count} 杯
                                                </span>
                                                <span className="text-[10px] text-milk-tea-400 flex items-center">
                                                    上次: {new Date(drink.latestTimestamp).toLocaleDateString()}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="text-right">
                                        <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(drink.avgRating)}</span>
                                        <p className="text-[10px] text-milk-tea-400 font-medium mt-0.5">
                                            {drink.avgRating.toFixed(1)}
                                        </p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            )
        })}

        {/* Search Results (Flat List) */}
        {searchQuery && filteredRecords.map(record => (
             <div key={record.id} className="bg-white p-4 rounded-2xl flex gap-4 shadow-sm shadow-milk-tea-100 border border-transparent">
                <div className="w-14 h-14 rounded-xl bg-milk-tea-100 flex-shrink-0 overflow-hidden relative">
                    {record.imageUrl ? (
                        <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
                    ) : (
                        <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-xl font-bold opacity-50">
                            {record.brand[0]}
                        </div>
                    )}
                </div>
                <div className="flex-1 min-w-0">
                    <div className="flex justify-between items-start">
                        <div>
                            <h4 className="font-bold text-milk-tea-900 truncate">{record.name}</h4>
                            <p className="text-xs text-milk-tea-500 font-medium uppercase tracking-wide">{record.brand}</p>
                        </div>
                        <span className="text-yellow-400 text-xs tracking-tighter">{renderStars(record.rating)}</span>
                    </div>
                    <div className="mt-1 flex flex-wrap gap-2">
                         <p className="text-[11px] text-milk-tea-400">
                             {record.sugar} • {record.ice}
                         </p>
                    </div>
                </div>
             </div>
        ))}

        {records.length > 0 && searchQuery && filteredRecords.length === 0 && (
             <div className="text-center text-milk-tea-300 mt-10">
                <p>未找到匹配项 "{searchQuery}"</p>
            </div>
        )}

        {records.length === 0 && (
            <div className="text-center text-milk-tea-300 mt-20 flex flex-col items-center">
                <p>图鉴是空的。</p>
                <p className="text-sm">快去喝一杯吧！</p>
            </div>
        )}
      </div>
    </div>
  );
};

import React from 'react';
import { Calendar, Plus, BarChart2, Coffee } from 'lucide-react';
import { Screen } from '../types';

interface NavigationProps {
  currentScreen: Screen;
  onNavigate: (screen: Screen) => void;
}

export const Navigation: React.FC<NavigationProps> = ({ currentScreen, onNavigate }) => {
  const getIconClass = (screenName: string) => {
      const isActive = currentScreen === screenName;
      return `flex flex-col items-center gap-0.5 w-12 transition-all duration-300 ${
        isActive ? 'text-milk-tea-800 scale-110' : 'text-milk-tea-300 hover:text-milk-tea-500'
      }`;
  };

  const getStrokeWidth = (screenName: string) => currentScreen === screenName ? 2.5 : 2;

  return (
    <div className="fixed bottom-8 left-6 right-6 z-40">
      <div className="bg-white/90 backdrop-blur-xl rounded-full shadow-[0_8px_30px_rgba(0,0,0,0.06)] border border-white/50 p-2 pl-6 pr-6 flex justify-between items-center h-16">

        {/* Diary */}
        <button
          onClick={() => onNavigate('calendar')}
          className={getIconClass('calendar')}
        >
          <Calendar size={20} strokeWidth={getStrokeWidth('calendar')} />
          <span className="text-[9px] font-bold tracking-wide">日历</span>
        </button>

        {/* Add (Record) - Dark Circle Style */}
        <button
           onClick={() => onNavigate('record')}
           className="flex items-center justify-center w-12 h-12 bg-milk-tea-800 text-cream rounded-full shadow-lg shadow-milk-tea-800/20 transform transition-all duration-200 active:scale-95 hover:scale-105"
         >
           <Plus size={24} strokeWidth={2.5} />
        </button>

        {/* Tasting */}
        <button
            onClick={() => onNavigate('library')}
            className={getIconClass('library')}
        >
            <Coffee size={20} strokeWidth={getStrokeWidth('library')} />
            <span className="text-[9px] font-bold tracking-wide">图鉴</span>
        </button>

        {/* Stats */}
        <button
            onClick={() => onNavigate('stats')}
            className={getIconClass('stats')}
        >
            <BarChart2 size={20} strokeWidth={getStrokeWidth('stats')} />
            <span className="text-[9px] font-bold tracking-wide">统计</span>
        </button>

      </div>
    </div>
  );
};

import React from 'react';
import { X, Calendar, Clock, Tag } from 'lucide-react';
import { TeaRecord } from '../types';

interface RecordDetailModalProps {
  record: TeaRecord;
  onClose: () => void;
}

export const RecordDetailModal: React.FC<RecordDetailModalProps> = ({ record, onClose }) => {
  const dateObj = new Date(record.timestamp);
  const dateStr = dateObj.toLocaleDateString('zh-CN', { weekday: 'short', year: 'numeric', month: 'short', day: 'numeric' });
  const timeStr = dateObj.toLocaleTimeString('zh-CN', { hour: '2-digit', minute: '2-digit' });

  const renderStars = (rating: number) => {
    return (
      <div className="flex gap-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <span key={star} className={`text-xl ${star <= rating ? 'text-yellow-400' : 'text-gray-200'}`}>★</span>
        ))}
      </div>
    );
  };

  return (
    <div className="fixed inset-0 z-50 flex items-end justify-center pointer-events-none">
      {/* Backdrop */}
      <div 
        className="absolute inset-0 bg-black/20 backdrop-blur-sm pointer-events-auto transition-opacity" 
        onClick={onClose}
      />

      {/* Modal Content */}
      <div className="bg-white w-full max-w-lg rounded-t-3xl shadow-[0_-10px_40px_rgba(0,0,0,0.1)] pointer-events-auto animate-slide-up overflow-hidden flex flex-col max-h-[90vh]">

        {/* Header Image Area */}
        <div className="relative h-56 bg-milk-tea-100 w-full shrink-0">
          {record.imageUrl ? (
            <img src={record.imageUrl} alt={record.name} className="w-full h-full object-cover" />
          ) : (
            <div className="w-full h-full flex items-center justify-center text-milk-tea-300 text-4xl font-bold opacity-30">
              {record.brand}
            </div>
          )}
          <button 
            onClick={onClose}
            className="absolute top-4 right-4 w-8 h-8 bg-black/20 hover:bg-black/30 backdrop-blur-md rounded-full text-white flex items-center justify-center transition-colors"
          >
            <X size={16} />
          </button>
        </div>

        {/* Content Scrollable */}
        <div className="p-6 overflow-y-auto">
          {/* Header Info */}
          <div className="flex justify-between items-start mb-4">
            <div>
              <p className="text-sm font-bold text-milk-tea-500 uppercase tracking-wide mb-1">{record.brand}</p>
              <h2 className="text-2xl font-bold text-milk-tea-900 leading-tight">{record.name}</h2>
            </div>
            <div className="text-right">
              <div className="text-xl font-bold text-milk-tea-800">¥{record.price}</div>
              {renderStars(record.rating)}
            </div>
          </div>

          {/* Time */}
          <div className="flex items-center gap-4 text-sm text-milk-tea-500 mb-6 bg-cream p-3 rounded-xl">
             <div className="flex items-center gap-1.5">
                <Calendar size={14} />
                <span>{dateStr}</span>
             </div>
             <div className="w-px h-3 bg-milk-tea-200"></div>
             <div className="flex items-center gap-1.5">
                <Clock size={14} />
                <span>{timeStr}</span>
             </div>
          </div>

          {/* Specs */}
          <div className="grid grid-cols-2 gap-3 mb-6">
            <div className="p-3 border border-milk-tea-100 rounded-xl bg-white">
                <p className="text-[10px] uppercase text-milk-tea-400 font-bold mb-1">甜度</p>
                <p className="text-milk-tea-800 font-medium">{record.sugar || '标准'}</p>
            </div>
            <div className="p-3 border border-milk-tea-100 rounded-xl bg-white">
                <p className="text-[10px] uppercase text-milk-tea-400 font-bold mb-1">温度/冰量</p>
                <p className="text-milk-tea-800 font-medium">{record.ice || '标准'}</p>
            </div>
          </div>

          {/* Tags */}
          {record.tags && record.tags.length > 0 && (
             <div className="mb-6">
                 <h3 className="text-xs font-bold text-milk-tea-400 uppercase mb-2 flex items-center gap-1">
                    <Tag size={12} /> 小料/备注
                 </h3>
                 <div className="flex flex-wrap gap-2">
                    {record.tags.map((tag, i) => (
                        <span key={i} className="px-3 py-1 bg-milk-tea-100 text-milk-tea-800 rounded-full text-xs font-medium">
                            {tag}
                        </span>
                    ))}
                 </div>
             </div>
          )}

          {/* Remark */}
          {record.remark && (
            <div className="mb-8">
                <h3 className="text-xs font-bold text-milk-tea-400 uppercase mb-2">饮用评价</h3>
                <div className="p-4 bg-cream rounded-2xl text-milk-tea-800 text-sm leading-relaxed italic border border-milk-tea-50">
                    "{record.remark}"
                </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
